vec3 shade(vec3 point, vec3 normal, vec3 eyeDir, float distToCamera, float sweepFactor, float fragmentNoise, vec3 rayDir) {
	// Init VRayMtl with defaults
	VRayMtlInitParams initParams;
	initParams.Vw = normalize(eyeDir);
