#define hue(a) .55 + .55 * cos(PI2* a * vec3(.35,.25,2.));

float hash21(vec2 p){  return fract(sin(dot(p, vec2(37.608, 67.584)))*53758.51453); }

void getMouse( inout vec3 p ) {
