//@iq
mat2 trs;
float sdTorus( vec3 p, vec2 t, float a ) {
  if(a>1.){
      p.xy *= trs;
      p.yz *= trs;
  }
  vec2 q = vec2(length(p.xy)-t.x,p.z);
  return length(q)-t.y;
}

vec3 shp,fhp;
vec3 sip,bip;
float thsh,fhsh;
mat2 t90;

const float size = 2./SCALE;
const float hlf = size/3.;
const float shorten = 2.26;   

vec2 map(vec3 q3){
    vec2 res = vec2(200.,1.);

    float k = 6.0/dot(q3,q3); 
    q3 *= k;

    q3.z += T*.325;

    float d = 2e6, t = 2e6, f = 2e6, g = 2e6;

    vec3 qid=floor((q3+hlf)/size);
    vec3 qm = mod(q3+hlf,size)-hlf;

    q3+=hlf;

    vec3 did=floor((q3+hlf)/size);
    vec3 qd = mod(q3+hlf,size)-hlf;

    float ht = hash21(qid.xy+qid.z);
    float hy = hash21(did.xz);

    // truchet build parts
    float thx = (.085+.035*sin((q3.y+qid.z)*4.15) ) *size;
    float thz = (.085+.035*sin(T*5.+(q3.y+did.x)*4.45) ) *size;

    if(ht>.6) qm.x *= -2.;
    if(hy>.6) qd.z *= -2.;

    float ti = min(
      sdTorus(qm-vec3(hlf,hlf,.1),vec2(hlf,thx),1.),
      sdTorus(qm-vec3(-hlf,-hlf,.1),vec2(hlf,thx),1.)
    );

    // truchet
    if(ti<t) {
        t = ti;
        bip = qid;
        fhp = qm;
    }

    float gi = min(
      sdTorus(qm.xzy-vec3(.1,1,hlf),vec2(.3,.035),1.),
      sdTorus(qm.xzy-vec3(.1,1,-hlf),vec2(.3,.035),1.)
    );

    if(gi<g) {
        g = gi;
        sip = qid;
        shp = qm;
    }

    qd.xz*=t90;
    float di = min(
      sdTorus(qd-vec3(hlf,hlf,.1),vec2(hlf,thz),1.),
      sdTorus(qd-vec3(-hlf,-hlf,.1),vec2(hlf,thz),1.)
    );

   // truchet
    if(di<d) {
        d = di;
        sip = did;
        shp = qd;
    }

    float fi = min(
      sdTorus(qd.xzy-vec3(.1,1,hlf),vec2(.3,.035),1.),
      sdTorus(qd.xzy-vec3(.1,1,-hlf),vec2(.3,.035),1.)
    );

    if(fi<f) {
        f = fi;
        sip = did;
        shp = qd;
    }
    if(d<res.x) res = vec2(d,2.);
    if(t<res.x) res = vec2(t,3.);
    if(f<res.x) res = vec2(f,4.);
    if(g<res.x) res = vec2(g,4.);
    float mul = 2.0/k;
    res.x = res.x * mul / shorten;

    return res;
}

vec2 marcher(vec3 ro, vec3 rd, int maxsteps) {
    float d = 1.;
    float m = -2.;
    for(int i=1;i<maxsteps;i++){
        vec2 t = map(ro + rd * d);
        if(t.x<d*MIN_DIST||d>MAX_DIST) break;
        d += i < 42 ? t.x*.45 : t.x*.95;
        m  = t.y;
    }
    return vec2(d,m);
}

// Tetrahedron technique @iq
vec3 getNormal(vec3 p, float t){
    float e = MIN_DIST *t;
    vec2 h = vec2(2.,-2.)*.6773;
    return normalize( h.xyy*map( p + h.xyy*e ).x + 
                      h.yyx*map( p + h.yyx*e ).x + 
                      h.yxy*map( p + h.yxy*e ).x + 
                      h.xxx*map( p + h.xxx*e ).x );
}


// softshadow www.pouet.net
float softshadow( vec3 ro, vec3 rd, float mint, float maxt, float k ){
    float res = 2.0;
    for( float t=mint; t < maxt; ){
        float h = map(ro + rd*t).x;
        if( h<0.002 ) return 0.3;
        res = min( res, k*h/t );
        t += h;
    }
    return res+0.2;
}

vec3 getSpec(vec3 p, vec3 n, vec3 l, vec3 ro) {
    vec3 spec = vec3(-1.);
    float strength = 0.85;
    vec3 view = normalize(p - ro);
    vec3 ref = reflect(l, n);
    float specValue = pow(max(dot(view, ref), -1.), 42.);
    return spec + strength * specValue;
}

vec3 camera(vec3 lp, vec3 ro, vec2 uv) {
    vec3 cf = normalize(lp - ro),
         cr = normalize(cross(vec3(1,2,1),cf)),
         cu = normalize(cross(cf,cr)),
         c  = ro + cf *.95,
         i  = c + uv.x * cr + uv.y * cu,
         rd = i - ro;
    return rd;
}

vec3 thp,ghp;
