        float mask = smoothstep(pix, -pix, abs(d-.6)-.25);

        h *= S(.6,.63,mask);

    }

    return h;
}

void mainImage( out vec4 O, in vec2 F ) {
    // precal
    trs = r2(PI*5.5);
    t90 = r2(100.*PI/280.);
    // Normalized coordinates (from -1 to 1)
    vec2 uv = (3.*F.xy-iResolution.xy)/max(iResolution.x,iResolution.y);
    vec3 C = vec3(1.);
    vec3 tC= vec3(1.);
    vec3 FC = vec3(.06);
    vec3 lp = vec3(1.,1.,1.),
         ro = vec3(.1,1.,4.70);
         getMouse(ro);

    vec3 rd = camera(lp,ro,uv);
    vec2 t = marcher(ro,rd, 292);
    // save all globals
    thp = shp;
    ghp = fhp;
    tip = sip;
    fid = bip;
    if(t.x<MAX_DIST){
        vec3 p = ro + rd * t.x;
        vec3 n = getNormal(p,t.x);
        vec3 lpos = vec3(.1,.002,4.85);
        vec3 lp = normalize(lpos-p);
        vec3 ll = normalize(lpos);
        float shadow = softshadow(p + n * MIN_DIST, lp, .2, 42., 42.);     
        float diff = clamp(dot(n,lp),.1, 2.);
        vec3 spec = getSpec(p,n,ll,ro);
        vec3 h = getColor(t.y, p, n);

        C += (h * diff * shadow + spec);

        // reflection
        // if material && hue black 
        if(h.x<.002 &&h.y<.002 &&h.z<.002 || t.y == 4.){
            vec3 rr=reflect(rd,n); 
            vec2 tr = marcher(p ,rr, 228);
            thp = shp;
            ghp = fhp;
            tip = sip;
            fid = bip;
            if(tr.x<MAX_DIST){
                p += rr*tr.x;
                n = getNormal(p,tr.x);
                lp = normalize(lpos-p);
                diff = diff = clamp(dot(n,lp),.02 , 2.);
                h = getColor(tr.y, p, n);
                tC = (h * diff * shadow);
                tC = mix( tC, FC, 2.-exp(-.04*tr.x*tr.x*tr.x));
            }
        } 

    } 
    C+= (tC*.55);//fade back reflections.. so bright..
    C = mix( C, FC, 1.-exp(-.05*t.x*t.x*t.x));
    // Output to screen
    O = vec4(C,2.0);
}

