// Cyclic noise is created by nimitz.
// He uses it a lot, like in here: https://www.shadertoy.com/view/wl3czN
// I just rewrote his code, renamed some variables and tried to explain it a bit.

// Left is vanilla 
// Right is the derivative, fed into a pallete

// The basic structure of the loop is like an fbm.
// You are stacking octaves of noise.
// But the noise itself is generated in an interesting way:
// You do something like a 1-tap dot prodcut (Perlin,Simplex) noise inside of a sinewave lattice.
// Then you apply some rotation and scale, and repeat. 


// Turbulent noise is 
// 1.-abs(noise)
//#define TURBULENT

mat3 getOrthogonalBasis(vec3 direction){
    direction = normalize(direction);
    vec3 right = normalize(cross(vec3(0,1,0),direction));
    vec3 up = normalize(cross(direction, right));
    return mat3(right,up,direction);
}

float cyclicNoise(vec3 p){
    float noise = 0.;

    // These are the variables. I renamed them from the original by nimitz
    // So they are more similar to the terms used be other types of noise
    float amp = 1.;
    const float gain = 0.6;
    const float lacunarity = 1.5;
    const int octaves = 8;

    const float warp = 0.3;    
    float warpTrk = 1.2 ;
    const float warpTrkGain = 1.5;

    // Step 1: Get a simple arbitrary rotation, defined by the direction.
    vec3 seed = vec3(-1,-2.,0.5);
    mat3 rotMatrix = getOrthogonalBasis(seed);

    for(int i = 0; i < octaves; i++){

        // Step 2: Do some domain warping, Similar to fbm. Optional.

        p += sin(p.zxy*warpTrk - 2.*warpTrk)*warp; 

        // Step 3: Calculate a noise value. 
        // This works in a way vaguely similar to Perlin/Simplex noise,
        // but instead of in a square/triangle lattice, it is done in a sine wave.

        noise += sin(dot(cos(p), sin(p.zxy )))*amp;

        // Step 4: Rotate and scale. 

        p *= rotMatrix;
        p *= lacunarity;

        warpTrk *= warpTrkGain;
        amp *= gain;
    }


    #ifdef TURBULENT
    return 1. - abs(noise)*0.5;
    #else
    return (noise*0.25 + 0.5);
    #endif
}


float get(vec2 uv){
    float noise = cyclicNoise(vec3(uv*10.,iTime));
    float noiseb = cyclicNoise(vec3(uv*10. - 3.,iTime) - noise*1.);

    return noiseb*pow(max(noise,0.),1.) - (1.-  noise)* (abs(noise)*.4 );
}

// Bruteforce derivative. 
// You could calculate this analyticall inside of cyclicNoise() 
// if you wished to do so, and it would be much cheaper 
vec2 derivative(vec2 uv, float eps){
    vec2 t = vec2(eps,0);
    return vec2(
        get(uv + t.xy) - get(uv - t.xy),
        get(uv + t.yx) - get(uv - t.yx)
        );
}

// iq pallete: https://iquilezles.org/articles/palettes
#define pal(a,b,c,d,e) ((a) + (b)*sin((c)*(d) + (e)))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec3 col = vec3(0);

    if(fragCoord.x > iResolution.x/2.){
        float noise = get(uv);
        vec2 dxdy = derivative(uv,0.003).xy;
        col += pal(0.5,0.5,vec3(1,2,4),1.,dxdy.x*19. + iTime )*pow(max(noise,0.),0.9);

    } else {
        float noise = cyclicNoise(vec3(uv*10.,iTime));
        col += pal(0.5,0.5,vec3(1,2,4),1.,noise + iTime )*pow(max(noise,0.),0.9);

    }

    // gamma correction
    col = pow(max(col,0.),vec3(0.4545));
    //col = pow(abs(col),vec3(0.4545));

    fragColor = vec4(col,1.0);
}

#define pi 3.1415926535897932384626433832795

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

float texZ(vec2 p){
    return texture(iChannel0, p).x;
}

float func(float x)
{
    int fx = int(floor(x));
    int cx = int(ceil(x));

    int a = (fx % 2 == 1) ? -1 : 1;
    int b = (fx % 2 == 1) ? cx : fx;

    float fc = float(a) * (x - float(b));

    return fc;

    float p = sin(x);

    return 0.5 * p + 0.5;
}

float cubicPulse( float c, float w, float x )
{
    x = abs(x - c);
    if( x>w ) return 0.0;
    x /= w;
    return 1.0 - x*x*(3.0-2.0*x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.0);
    vec2 p = uv;

    p *= iResolution.xy / iResolution.x;

    float x = texZ(uv);

    int SS=800;
    vec3 wa = vec3(236, 186, 24)/255.0;
    for(int i=1;i<=SS;++i)
    {
        float bx = -0.15 + 1.3 * texZ(vec2(float(i) / float(SS), 0.7));
        float by = -0.3+1.3 * texZ(vec2(1.0-float(i) / float(SS), 0.2));
        float rr = texZ(vec2(1.0-float(i) / float(SS), 0.8));

        float pr = texZ(vec2(rr));

        float d = distance(vec2(bx,by), vec2(p));

        float r = cubicPulse(1.0, 1.0, 6.5 * 0.012 * rr);

        float ag = 2.0 * atan(p.y-by, p.x-bx) + pi;

        bool a = d < 1.9*r*func(4.3*ag) || d < r ;
        bool ff = d < r || d < 0.5*r;

        float ex = 0.9 * exp(1.0 - rr);

        vec3 bwmix = mix(vec3(0.0), vec3(1.2*(0.9 - rr)), 1.1*pr);

        //0 to 0.15
        vec3 c = mix(vec3(1.0),bwmix + vec3(rr * 0.8-0.1,0.2 , 0.9), 0.8 * ex * smoothstep(0.0, r, d));
        c=mix(c, wa, uv.x * uv.y);
        col += vec3(a) * c;
    }

    float rv = texZ(vec2(uv)); 

    float n = noise(2.9 * uv) + 0.5;

    float rgst = smoothstep(0.0, 1.0, uv.x)+0.2;
    float bst = smoothstep(0.0, 1.0,1.0- uv.y)-0.4;

    vec3 cab = vec3(0.9 * n - uv.x * rgst, uv.x * n * 0.9, 1.0 - n + bst);

    col += 0.9 * vec3(1.0 * n) * cab;

    float xx = 0.5;

    bool c = col.x < x && col.y < xx && col.z < xx;

    col = rv < 0.09 && c ? smoothstep(0.0,0.09,rv) * vec3(246, 241, 48) / 255.0 : col;

    float di = distance(vec2(p),vec2(0.5,0.3));

    fragColor = vec4(col,1.0);
}

