// ##### RAYMARCHING #####
vec3 directLight(vec3 raypos, float dither){
    vec3 LightColor = vec3(1.0);
    #ifdef realtime
    vec3 LightDirection = normalize(vec3(1.0, -0.5, 1.0));
    #endif
    #ifndef realtime
    vec3 LightDirection = normalize(vec3(1.0, -0.5, 1.0)+((rand4().yzw-0.5)*1.5));
    #endif
    float distorigin = 0.0;
    vec3 density = vec3(0.0);
    vec3 attenuation = vec3(1.0);
    raypos += (LightDirection*(dither-0.5)*STEP_SIZE);
    for(int i=0; i<MAX_MARCHES; i++){
        raypos = raypos+(LightDirection*STEP_SIZE);
        density = nebulanoise(raypos)*DENSITY;
        vec3 absorbance = exp(-density*STEP_SIZE);
        attenuation *= absorbance;}
    return LightColor*attenuation;
}

// Compute/March the Ray
vec3 raymarch(vec3 camerapos, vec3 raydir, vec2 coord){
    float distorigin = 0.0;
    #ifdef realtime
    float dither = rand4blue().x;
    vec3 raypos = camerapos+(raydir*((dither-0.5)*2.0)*STEP_SIZE);
    #endif
    #ifndef realtime
    float dither = rand();
    vec3 raypos = camerapos+(raydir*((rand()-0.5)*2.0)*STEP_SIZE);
    #endif
    vec3 outCol = vec3(0.0);
    vec3 attenuation = vec3(1.0);
    for(int i=0; i<MAX_MARCHES; i++){
        raypos = raypos+(raydir*STEP_SIZE);
        vec3 density = nebulanoise(raypos)*DENSITY;
        vec3 absorbance = exp(-density*STEP_SIZE);
        vec3 transmittance = 1.0-absorbance;
        vec3 directlighting = attenuation*transmittance;
        if((density.r+density.g+density.b) > 0.0){
            directlighting = directlighting*directLight(raypos, dither);
        }
        outCol += directlighting;
        attenuation *= absorbance;
        distorigin = distance(raypos, camerapos);
        if(distorigin>MAX_DISTANCE) break;
    }
    return outCol;
}

