#define sat(a) clamp(a, 0., 1.)
mat2 r2d(float a){float sa = sin(a);float ca=cos(a);return mat2(ca,sa,-sa,ca);}
float lenny(vec2 v){return abs(v.x)+abs(v.y);}

bool inTriangle(vec2 A, vec2 B, vec2 C, vec2 P, float thick)
{
  vec2 v0 = C - A;
  vec2 v1 = B - A;
  vec2 v2 = P - A; 

  // Compute dot products
  float dot00 = dot(v0, v0);
  float dot01 = dot(v0, v1);
  float dot02 = dot(v0, v2);
  float dot11 = dot(v1, v1);
  float dot12 = dot(v1, v2);

  // Compute barycentric coordinates
  float invDenom = 1. / (dot00 * dot11 - dot01 * dot01);
  float u = (dot11 * dot02 - dot01 * dot12) * invDenom;
  float v = (dot00 * dot12 - dot01 * dot02) * invDenom;
  // Check if point is in triangle return
  return (u >= 0.) && (v >= 0.) && (u + v < 1.) &&
   (u<thick||v<thick||(u+v)>1.-thick);
}

vec3 drawSphLight(vec2 uv, vec3 norm)
{
  vec3 ldir = vec3(-5.);

  return sat(-dot(normalize(norm),normalize(ldir)))*vec3(1.);
}

vec3 draw(vec2 uv)
{
  uv.y += sin(iTime)*.5;
  vec3 col;
  float gstp = 0.1;
  float gsz = gstp/10.;
  float limit = 1.-float(abs(uv.y)<.3);
  col = vec3(.34,.45,.67)*limit*float(mod(sign(uv.x)*iTime+(abs(uv.x)+gsz/4.)/abs(uv.y),gstp)<gsz
    || mod(1./abs(uv.y),gstp)<gsz);

  col += (1.-sat((lenny(uv*vec2(.2,2.))-.5)*2.))*vec3(1.);

  vec2 sphuv = uv+vec2(0.,sin(iTime+3.));
  float sphSdf = (length(sphuv)-.5*1./(sin(iTime*2.)+2.5));
  float sph = 1.-sat(sphSdf*200.);

  vec2 tp = vec2(-0.,-.5);
  float tri = float(inTriangle(.5*vec2(sin(-iTime),cos(iTime)),
    vec2(.5,.2)*vec2(sin(iTime*.5),cos(iTime*2.)), vec2(sin(iTime),cos(iTime))*vec2(.5,.8),uv-tp,.5));

    vec3 tris = tri*vec3(uv,.5);

  return 2.*tris+mix(col,drawSphLight(uv, vec3(sphuv*5., 1.-sat(sphSdf))),sph);
}

vec3 rdr(vec2 uv)
{
  vec3 col = draw(uv);
  col *= 1.5;
  col *= (1.-sat((lenny(uv)-.8)*1.))*(vec3(uv,.5)*.5+.5);
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord.xy / iResolution.xx;
  uv -= vec2(.5)*iResolution.xy/iResolution.xx;
  uv*= 4.;

  vec3 col = rdr(uv);
  fragColor = vec4(col, 1.0);
}

