}

float rand( vec2 uv )
{
	float t = fract( iTime );
	return nrand( uv + 0.07*t );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float du = 1. / iResolution.x;
    float dv = 1. / iResolution.y;

	vec2 uv = fragCoord/iResolution.xy;

	float y = texture(iChannel0,uv).x;
    // food
    float f = texture(iChannel0,uv).z;

    // sum of surrounding y's
    float s = 1./12. * texture(iChannel0,uv + vec2(-du,-dv)).x +
              1./6.  * texture(iChannel0,uv + vec2(-du,0)).x + 
              1./12. * texture(iChannel0,uv + vec2(-du,dv)).x + 
              1./6.  * texture(iChannel0,uv + vec2(0,-dv)).x + 
              1./6.  * texture(iChannel0,uv + vec2(0,dv)).x +
              1./12. * texture(iChannel0,uv + vec2(du,-dv)).x +
              1./6.  * texture(iChannel0,uv + vec2(du,0)).x + 
              1./12. * texture(iChannel0,uv + vec2(du,dv)).x;


    // Sorry for the lack of comments
    // But I dont understand it either

    if (f > 0.8){
        y += 0.02 * y;
        f -= 0.02;
    }
    else{
        y -= 0.05 * y;
        f += 0.01;
    }

    if (s > sin(iTime*0.04)){
        y -= 0.1 + 0.05*sin(iTime*0.08);
    }
    else{

        y += 0.1 + 0.03*cos(iTime*0.063);
    }

    // Init
