// const
const float PI = 3.14159265359;

float sig(float x) { return x >= 0. ? +1. : -1.; }

// 2D transformations: vec2 => vec2
// complex
vec2 cmul(vec2 za,vec2 zb) // za*zb
{
    return za*mat2(zb.x,-zb.y,zb.yx);
}

vec2 cinv(vec2 z) // 1/z
{
    return vec2(z.x,-z.y)/dot(z,z);
}

vec2 cdiv(vec2 z, vec2 w) // z/w
{
    return cmul(z,cinv(w));
}

vec2 cpow(vec2 z, int n) // z^n
{
    vec2 w = z;
    for (int i = 1; i < n; i++){
    w = cmul(w,z);
    }
    return w;
}

vec2 crot(vec2 z,float a) // z*e^(i*a)
{
    return cmul(z, vec2(cos(-a),sin(-a)));
}

vec2 crpt(vec2 z,float a, int n, float x0) // (z*e^ia)^n-x0
{
	return cpow(crot(z, a), n) - vec2(x0, 0.);
}

// 3D-transformations: vec3 => vec3
//color
vec3 hsv2rgb(float h, float s, float v)  // hue, saturation, value
{
    vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.,4.,2.),6.)-3.)-1., 0., 1. );
    rgb = rgb*rgb*(3.-2.*rgb); // cubic smoothing
    return v * mix( vec3(1.), rgb, s);
}

vec2 isphere(in vec3 ro, in vec3 rd, in float r )
{
    // sphere centered at the origin, with size rd
	float b = dot(ro,rd);
	float c = dot(ro,ro) - r*r;
    float h = b*b - c;

    if( h<0. ) return vec2(-1.);

    h = sqrt( h );

    return -b + vec2(-h,h);
}

// https://www.shadertoy.com/view/fdfSDH
// mla https://www.shadertoy.com/view/lsGyzm
vec4 inverseStereographic(vec3 p) {
  float k = 2.0/(1.0+dot(p,p));
  return vec4(k*p,k-1.0);
}
vec3 stereographic(vec4 p4) {
  float k = 1.0/(1.0+p4.w);
  return k*p4.xyz;
}

vec3 rot4D(in vec3 p, in float t, in float r_inv){
    p /=r_inv;                             // normalize
    vec4 p4 = inverseStereographic(p);     // Project to 4d
    p4.zw = cmul(p4.zw, vec2(cos(-t),sin(-t)));// Rotate in the 4th dimension
    p = stereographic(p4);                 // Project back to 3d
    p *=r_inv;                             // unnormalize
    return p;
}
////////////////////////////////////////////////////////////////////////////////

float map( in vec3 p, out vec4 mat ){
    //// sdf's
    int k, n;
    vec3 pn = normalize(p);
    vec2 z;
    float r, au, av;
    float sr = 1.; // Torus


    //float r_inv = sqrt(abs(ba_sh_a)); // inversion bending radius (torus-spheres transformation)
    //p = rot4D(p, 0.125 *iTime, r_inv);

    /// cyclic regular Polyeder ba_id in {1,2,3}    
    if(  ba_id == 0 || ba_id == 1 || ba_id == 2 || ba_id == 3 ){    
		// Fk,2(z)
		if (ba_id == 0)
        {
            k = 7; n = 2;
            float a1 = float(k);            
            vec2 zk;
            if (pn.z < 0.) // stereographic (north pol)
            {
                z = pn.xy/(1.0-(pn.z));
                zk = cpow(z,k);
                z = a1 * cdiv(zk, cpow(zk - vec2(1.,0.) ,n));
            }else          // stereographic (south pol)
            {
                z = vec2(pn.x, -pn.y)/(1.+pn.z);
                zk = cpow(z,k);
                z = a1 * cdiv(zk, cpow(vec2(1.,0.) - zk ,n));     
            }
        }

		// F3,3(z)
		if (ba_id == 1)
        {
            k = 3; n = 3;
            float a1 = 2. * sqrt(2.);
            vec2 zk;
            if (pn.z < 0.) // stereographic (north pol)
            {
                z = pn.xy/(1.-pn.z);
                zk = cpow(z,k);
                z = 1./(8.*a1) * zk;
                z = cmul(z, cpow(zk - vec2(a1,   0.), k));
                z = cdiv(z, cpow(zk + vec2(1./a1,0.), n));
            }else          // stereographic (south pol)
            {
                z = vec2(pn.x, -pn.y)/(1.+pn.z);
                zk = cpow(z,k);
                z = 1./(8.*a1)*cinv(zk);       
                z = cmul(z, cpow(vec2(1.,0.) - zk * a1, k));
                z = cdiv(z, cpow(vec2(1.,0.) + zk / a1, n));    
            }
		}

		// F4,3(z)
		if (ba_id == 2)
        {
            k = 4; n = 3;
            float a1 = 108.;
            vec2 zk;
            if (pn.z < 0.) // stereographic (north pol)
            {        
                z = vec2(pn.x, pn.y)/(1.-pn.z);
                zk = cpow(z,k);
                z = a1 * zk;
                z = cmul(z, cpow(zk          - vec2(1.,0.),          k));
                z = cdiv(z, cpow(cmul(zk,zk) + 14.*zk + vec2(1.,0.), n));
            }else          // stereographic (south pol) 
            {
                z = vec2(pn.x, -pn.y)/(1.+pn.z);
                zk = cpow(z,k);
                z = a1 * zk;
                z = cmul(z, cpow(vec2(1.,0.) - zk,                   k));
                z = cdiv(z, cpow(vec2(1.,0.) + 14.*zk + cmul(zk,zk), n)); 
            }
		}

		// F5,3(z)
		if (ba_id == 3)
        {
            k = 5; n = 3;
            float a1 = 1728.;
            vec2 zk, zk2;
            if (pn.z < 0.) // stereographic (north pol)
            {  
                z = pn.xy/(1.+abs(pn.z));
                zk = cpow(z,k); 
                zk2 = cmul(zk,zk);
                z = a1 * zk;
                z = cmul(z, cpow(zk2 - 11. * zk - vec2(1.,0.), k));
                z = cdiv(z, cpow(cmul(zk2,zk2)
                               +228.*cmul(zk2,zk)
                               +494.*zk2
                               -228.*zk
                               +vec2(1.,0.), n));
            } else         // stereographic (south pol)                    
            {
                z = vec2(pn.x, -pn.y)/(1.+pn.z);
                zk = cpow(z,k);
                zk2 = cmul(zk,zk);
                z = a1 * zk; 
                z = cmul(z, cpow(vec2(1.,0.) - 11. * zk - zk2, k));
                z = cdiv(z, cpow(vec2(1.,0.)
                               +228.*zk
                               +494.*zk2
                               -228.*cmul(zk2,zk)
                               +cmul(zk2,zk2), n));
            }
		}

        // sense of rotation of the polygon: sig(sr) in {-1.,+1.}
        // number of sides of the polygon: abs(sr) in {n,k}
        sr = float(length(z)>1.0?-n:k);

		// au
		au = atan(z.y,z.x) - .5 * PI * sig(sr);

		// av       
		float f_v_distri = .5 * ba_sh_a / ba_v_distri;        
		f_v_distri = sqrt( 1. + pow( f_v_distri ,2.)) + f_v_distri;

		// length(w) in [0., 1., +inf] => [-inf, 0., +inf] => av in [-0.5*PI,+0.5*PI]
		av = atan(log(length(z))*f_v_distri);

		// spherical => cartesian
		p = length(p)
          * vec3( sin(av) * cos(au),
				  sin(av) * sin(au),
				  cos(av) );
	}

	/// Torus:
	// 3D-space: cartesian3D => cylinder3D transformation
	au = atan(p.y, p.x); //float in [-PI, +PI]
	r = length(p.xy);	 //float in [0., +inf]
	// 2D-space: complex plane := radial plane in cylinderc coordinates
	z = vec2(r, p.z);

	/// 2Djulia :realaxis-translation, fraction == 2 => (torus,sphere,2spheres)
	z = cmul(z,z); z.x -= ba_sh_a;
	float ssr = sig(sr);

	//float fru = 1.0; // fractions in u-direction
	/// 2Djulia realaxis-translation,fraction,rotation,torsion
	if (j1_ON){
	float j1_sh = j1_sh_a0 + j1_sh_a1 * cos(float(j1_sh_f1) *(au - PI*(j1_sh_p1+j1_sh_pv1*iTime)));
	float j1_to = ssr * (au*float(j1_to_c)/float(j1_to_f) + PI*(j1_to_p+j1_to_pv*iTime));
	z = crpt(z, j1_to, j1_to_f,  j1_sh);
	//fru *= (j1_sh_f1 == 0)?1.0:float(j1_sh_f1);
	}

	/// 2Djulia realaxis-translation,fraction,rotation, torsion
	if (j2_ON){
	float j2_sh = j2_sh_a0 + j2_sh_a1 * cos(float(j2_sh_f1) * (au - PI*(j2_sh_p1+j2_sh_pv1*iTime)));
	float j2_to = ssr * (au*float(j2_to_c)/float(j2_to_f) + PI*(j2_to_p+j2_to_pv*iTime));
	z = crpt(z, j2_to, j2_to_f,  j2_sh);
	//fru *= (j2_sh_f1 == 0)?1.0:float(j2_sh_f1);
	}

	mat = vec4(z,au,sr);
	float res = log(length(z)/end_r0); // TODO:  :( Not perfect!

	return res;
	}

float intersect( in vec3 ro, in vec3 rd, out vec4 rescol, in float px )
{
	float res = -1.0; // init no rayintersection

	// boundingshape
	vec2 dis = isphere( ro, rd ,abs(bb_size));
	if( dis.y<0.0 ) // Does ray hit boundingshape?
		return -1.0;
	dis.x = max( dis.x, max(rm_rlmin,0.0) );// start_raylength from bb_near_hit or raylength_minimum
	dis.y = min( dis.y, 10.0 );	// end_raylength from bb_far_hit or raylength_maximum
	// raymarch signed distance field
	vec4 data; // data from surface hit point and accumulated data while raymarching

	float fh = (0.5-0.0001)*rm_p3slmul*rm_p3slmul*rm_p3slmul + 0.0001; // slider response curve
	float t = dis.x;
	for( int i=0; i<rm_imax; i++  )
	{
		vec3 pos = ro + rd*t;
		float th = 0.0001*px*t; // delta_sdf_surface_hit
		float h = map(pos, data);
		if( t>dis.y || h<th ) break; // ray is outside boundingshape OR sdf_surface_hit.
		t += h*fh; // step_length * step_length_multiplier
	}

	if( t<dis.y ) // Is ray inside boundingshape?
	{
		rescol = data; // return data
		res = t; // return ray_length
	}
	return res;
}

vec3 calcNormal( in vec3 pos, in float px )
{
	vec4 tmp; // dummy variable
	vec2 e = vec2(1.0,-1.0)*0.5773*0.25*px;
	return normalize( e.xyy*map( pos + e.xyy,tmp ) +
					  e.yyx*map( pos + e.yyx,tmp ) + 
					  e.yxy*map( pos + e.yxy,tmp ) + 
					  e.xxx*map( pos + e.xxx,tmp ) );
}

// from iq
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
	float t = mint;
	for( int i=0; i<1; i++ )
	{
		vec4 temp;
		float h = map( ro + rd*t, temp );
		res = min( res, 8.0*h/t );
		t += h*.25;
		if( h<0.001 || t>tmax ) break;
	}
	return clamp( res, 0., 1. );
}

// from iq
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
	float sca = 1.0;
	// antialeasing
	for( int i=0; i<1; i++ )
	{
		float hr = 0.01 + 0.12*float(i)/4.0;
		vec3 aopos =  nor * hr + pos;
		vec4 temp;
		float dd = map( aopos, temp );
		occ += -(dd-hr)*sca;
		sca *= 0.95;
	}
	return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );
}

// transform from mla
vec3 transform(in vec4 p){
	if (iMouse.x > 0.0)
	{
		float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
		float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
		p.yz = crot(p.yz,theta);
		p.zx = crot(p.zx,-phi);
	}
	//p.xz = crot(p.xz,p.w*0.1);
	return p.xyz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// time
	float time = iTime;
	// camera
	float fle = 2.0;
	vec3 ro = transform(vec4(0,0,-cam_dist,time)).xyz;
	vec2 uv = (2.0*(fragCoord-0.5*iResolution.xy))/iResolution.y;

    // ray direction
	vec3 rd = normalize(transform(vec4(uv,fle,time)));
	// get ray distance to (intersection) hit point
	vec4 mat = vec4(0.0);
	float px = 2.0/( iResolution.y*fle );
	float t = intersect( ro, rd, mat, px );

	// light
	const vec3 ld = 0.5*vec3(0.,-.5,-1.); // ligth direction
	const vec3 lc = vec3(0.4);// ligth color
	vec3 bg = vec3(0.8,0.9,1.0)*(0.6+0.4*rd.y);	// background-color

	// color
	vec3 col = vec3(0.6,0.4,0.7);

	if (t < 0.0){ // sky
		col = bg;
		col += 5.0*vec3(0.8,0.7,0.5)*pow( clamp(dot(rd,lc),0.0,1.0), 20.0 ); // sun
	}
    else{ // hit with object surface
        vec3 p = ro + rd * t;
		vec3 n = calcNormal( p, px );
		//vec2 mat_uv = vec2(mat.w, atan(mat.z, mat.y) ); //angle-u angle-v

		if(texture_ON){
			//texture_data
			vec2 z = mat.xy; float au = mat.z; float sr = mat.w; float ssr = sig(sr);

			// texture_coords
			float u = au/PI;
			float v = ssr*atan(z.y, z.x)/PI;

			// texture_color
			u -= j2_sh_pv1 * iTime;
			float col_h = 1.0; // h in [-0.5,0.0,+0.5] => [green_cyan,red,blue_cyan]
			//col_h *= 0.5*v;
			col_h *= 0.5*u;
			float col_s = 1.0;
			//col_s *= 0.9+0.1*ssr; // show polygon rotation sens in u-direction
			float col_v = 1.0;
			col_v *= .5+.5*fract(float(tex_u_subdiv)*1.0*(.5+.5*u));
			col_v *= .5+.5*fract(float(tex_v_subdiv)*2.0*(.5+.5*v));

			col = hsv2rgb(col_h,col_s,col_v);
		}
		else col = -n;

		if(shade_ON){
			// lighting
			float occ = calcAO( p, n );
			float amb = clamp( 0.5+0.5*n.y, 0.0, 1.0 );
			vec4 temp; // dummy variable
			float dif = clamp( dot( n, ld ), 0.0, 1.0 ) * map( p+n*1.16, temp);
			float spe = pow(clamp( dot( rd, ld ), 0.0, 1.0 ),16.0);
			float sss = map( p - n*0.001, temp)/0.01;
			// shading
			dif *= softshadow( p, ld, 0.1, 1. );
			vec3 brdf = vec3(0.0);
			brdf += 0.2*dif*vec3(1.00,0.90,0.60);
			brdf += 0.2*spe*vec3(0.8,0.60,0.20)*dif;
			brdf += 0.2*amb*vec3(0.40,0.60,0.40)*occ;
			brdf += 0.4;
			col.rgb *= brdf;
		}

		if(fog_ON){
			// fog
			col = mix( bg,col, exp( -0.025*t*t));
		}
	}
	// gamma
	col = sqrt(col);

    fragColor = vec4(col,1.);
}

