// ##### NOISE #####
// Cyclic Noise from https://www.shadertoy.com/view/wl3czN
const mat3 m3 = mat3(0.33338, 0.56034, -0.71817, -0.87887, 0.32651, -0.15323, 0.15162, 0.69596, 0.61339)*2.1;
void cyclicOctave(inout vec3 p, inout float rz, inout float z, inout float trk, inout float dspAmp){
    p += sin(p.yzx*0.25*trk - trk*6.1 + cos(p*0.1 + 0.5)*1.0)*dspAmp;
    float ofst = 4.6;
    vec3 s = sin(p*1.3);
    rz += smoothstep(-1.1, 0.5, dot(s, cos(p.yzx*0.95+s.xyz+ofst)))*z;
    dspAmp *= 0.65;
    z *= 0.45;
    trk *= 1.45;
    p *= m3;}
float cyclic3D(vec3 p){
    vec3 bp = p;
    float rz = 0.0;
    vec3 drv = vec3(0.0);  
    float z = 1.44;
    float trk= 1.0;
    float dspAmp = 1.;
    for (int i = 0; i<=NUM_OCTAVES; i++){
        cyclicOctave(p, rz, z, trk, dspAmp);}
    rz -= 1.1;
    return rz;}

// Nebula Noise
vec3 nebulanoise(vec3 raypos){
    float density = max(0.0, cyclic3D(raypos)-distance(raypos, vec3(0.0, 0.0, 0.0)));
    vec3 rgb = vec3(density);
    return rgb;
}

// ##### RAYMARCHING #####
vec3 directLight(vec3 raypos){
    vec3 LightColor = vec3(1.0);
    vec3 LightDirection = normalize(vec3(-1.0, -1.0, 1.0));
    float distorigin = 0.0;
    vec3 density = vec3(0.0);
    vec3 raydirmod = LightDirection*STEP_SIZE*rand();
    vec3 attenuation = vec3(1.0);
    for(int i=0; i<32; i++){
        raypos = raypos+raydirmod;
        density = vec3(nebulanoise(raypos)*DENSITY);
        vec3 absorbance = exp(-density);
        attenuation *= absorbance;}
    return LightColor*attenuation;
}

// Compute/March the Ray
vec3 raymarch(vec3 camerapos, vec3 raydir, vec2 coord){
    float distorigin = 0.0;
    //vec3 densityadd = vec3(0.0);
    //vec3 density = vec3(0.0);
    vec3 raypos = camerapos;
    vec3 raydirmod = (raydir*rand())*STEP_SIZE;
    vec3 outCol = vec3(0.0);
    vec3 attenuation = vec3(1.0);
    for(int i=0; i<MAX_MARCHES; i++){
        raypos = raypos+raydirmod;
        // LoicVDB: don't use this, the density doesn't accumulate, its
        // only purpose is to compute absorbance and transmittance
        //densityadd = vec3(fractal(raypos)*DENSITY);
        //density = vec3(density+densityadd);

        // LoicVDB: just use this for density instead:
        vec3 density = vec3(nebulanoise(raypos)*DENSITY);
        vec3 absorbance = exp(-density*STEP_SIZE);
        vec3 transmittance = 1.0-absorbance;
        vec3 directlighting = attenuation*transmittance;
        if((density.r+density.g+density.b) > 0.0){
            directlighting = directlighting*directLight(raypos);
        }
        outCol += directlighting;
        attenuation *= absorbance;
        distorigin = raypos.z-camerapos.z;
        if(distorigin>MAX_DISTANCE) break;
    }
    return outCol;//*attenuation;
}

// ##### RENDERING #####
// Render the Image
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    // LoicVDB: I store the number of samples in the alpha channel
    // LoicVDB: take the previous color and sample count (black and 0 if on first frame)
    vec4 oldFragColor = vec4(0.0);
    if(iFrame != 0) oldFragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
    // LoicVDB:I put it in the fragColor var because I want to keep it even if it's not in a tile
    fragColor = oldFragColor;

    // LoicVDB's Hyper Epic Tiles Function
    int index = int(fragCoord.x/iResolution.x*float(TILES))
              + int(fragCoord.y/iResolution.y*float(TILES))*TILES;
    if(iFrame-(TILES*TILES*(iFrame/(TILES*TILES))) != index) return;

    // Start RNG
    INIT_RNG;

    // Camera Orientation
    vec3 camerapos = vec3(2.0, -2.0, 2.0);
    #ifdef ROTATION_MATRIX
	    // Dumb rotation matrix hecking Michael made me make
	    float xrot = atan(sqrt(2.0));
	    float yrot = pi;
	    float zrot = -pi/4.0;
	    // Camera Orientation (Cursed)
        mat3 dir;
	    dir[0] = vec3(cos(yrot)*cos(zrot),-cos(yrot)*sin(zrot),sin(yrot));
	    dir[1] = vec3(cos(xrot)*sin(zrot)+sin(xrot)*sin(yrot)*cos(zrot),cos(xrot)*cos(zrot)-sin(xrot)*sin(yrot)*sin(zrot),-sin(xrot)*cos(yrot));
	    dir[2] = vec3(sin(xrot)*sin(zrot)-cos(xrot)*sin(yrot)*cos(zrot),sin(xrot)*cos(zrot)+cos(xrot)*sin(yrot)*sin(zrot),cos(xrot)*cos(yrot));
    #endif
    #ifndef ROTATION_MATRIX
        vec3 xdir = vec3(1.0,0.0,0.0);
        vec3 ydir = vec3(0.0,1.0,0.0);
        vec3 zdir = vec3(0.0,0.0,1.0);
    #endif

    // Undistorted Normalized Pixel Coordinates (From 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x;
    vec2 uvd = fragCoord/iResolution.xy;
    #ifdef ROTATION_MATRIX
    	vec3 raydir = normalize(FOV*(uv.x*dir[0]+uv.y*dir[1])+dir[2]);
    #endif
