// ^^^ Switch Between Common, Buffer A, and Buffer B ^^^
// This is for Exporting a 32-Bit Float OpenEXR using the Button on the Bottom of the Editor

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // LoicVDB: take the color from the A buffer
    vec4 texel = texelFetch(iChannel0, ivec2(fragCoord), 0);

    // LoicVDB: divide it by the sample count (the alpha channel)
    vec3 color = (texel.a == 0.0 ? vec3(0.0) : texel.rgb / texel.a);

    // Output to screen
    fragColor = vec4(pow(color, vec3(1.0/2.2)), 1.0);
}

