    float theta = atan(uv.y, uv.x);
    float thetanorm = (theta + pi)/(2.0*pi);

    // Shift r domain for aesthetics
    r = r + 0.1;
    r *= 1.0;
    float i = floor(r*r*370.0 + 0.02) + 1.0;

