    vec3 color2 = vec3(hash11(k+5.0),hash11(k+6.0),hash11(k+7.0));

    float cfbm = fbm(i/4.0, 2.0, 2) + 0.5;;
    float t = sin(iTime*5.0 + cfbm*99.0)*0.5 + 0.5;

    vec3 color = mix(color1, color2, t);




