    // polar coords
    uv = vec2(atan(uv.y,uv.x),length(uv));

    vec2 ouv = uv;
    uv += 0.5;
    uv.y *= pi;


    // parametrized sphere
    vec3 offs = vec3(cos(uv.y)*cos(uv.x),sin(uv.y),cos(uv.y)*sin(uv.x));

    // insert camera rotations here
    offs.yz *= rot(-(1.)*pi);

