vec2 getGround(vec3 p){
    float d = p.y;
    vec3 pp = vec3(p.x,4.,p.z)*0.6;
    pp.x *= 0.5;
    float n = turbulentNoise(pp, true);
    float on = n;
    n += turbulentNoise(pp*3., true)/2.*pow(n,0.4);
    n += turbulentNoise(pp*3. + 4., true)/2.*pow(n,0.2);
    n += turbulentNoise(pp*4., true)/2.*pow(n,0.4);
