}
float column(vec3 p, float l, float r)
{
    p.y -= l;
    float r2 = r*2.0;
    float r12 = (r+r2)/2.0;
    float cb = cube(vec3(p.x, p.y+(r/2.0), p.z), vec3(r2, r12/2.0, r2));
    cb = max(cb, -(length(vec3(p.x-r12, p.y+r12, p.z-r12))-(r2/1.25)) );
    cb = max(cb, -(length(vec3(p.x+r12, p.y+r12, p.z-r12))-(r2/1.25)) );
    cb = max(cb, -(length(vec3(p.x-r12, p.y+r12, p.z+r12))-(r2/1.25)) );
    cb = max(cb, -(length(vec3(p.x+r12, p.y+r12, p.z+r12))-(r2/1.25)) );

    float cy = sdCylinder(p, vec3(0.0), vec3(0.0,-l,0.0), r);
    //cy = max(cy, -boundingBox(vec3(p.x, p.y+l/2.0, p.z), vec3(r/1.25, l/2.0, r/1.25), 0.25));

    cb = min(cb, cy)-0.1;

    return cb;
}

