float box( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
vec2 mandelbulb( in vec3 p )
{
    float mdist = 1e12;
    vec3 w = p;
    float exp = 8.0;
    float m = dot(w,w);
	float dz = 1.0;


	for( int i=0; i<iters; i++ )
    {
        dz = exp*pow(sqrt(m),exp-1.0)*dz + 1.0;

        float r = length(w);
        float b = exp*acos( w.y/r);
        float a = exp*atan( w.x, w.z );
        w = p + pow(r,exp) * vec3( sin(b)*sin(a), cos(b), sin(b)*cos(a) ); 
        mdist = min(mdist, length(w));

        m = dot(w,w);
		if( m > 256.0 )
          break; 
    }

    float sde = 0.25*log(m)*sqrt(m)/dz;
    if(sliced == 1) {
    return vec2(max(sde,-box(p-vec3(2.0,0.0,0.0),vec3(2.0,2.0,2.0))), mdist);
    }
    else return vec2(sde,mdist);
}


