    }
    float colorI = (sin(pow(dist(k).y,0.3)*6.0)+1.0)/2.0;
    vec3 color = vec3(0.,0.,0.);
    vec3 control[5] = vec3[5](vec3(0.0, 0.02745, 0.39216), vec3(0.125, 0.41961, 0.79608), vec3(0.92941, 1.0, 1.0), vec3(1.0, 0.66667, 0.0), vec3(0.0, 0.00784, 0.0));
    if (colorI<0.16) {
    color = mix(control[0], control[1], colorI/0.16);
    } else if (colorI<0.42) {
    color = mix(control[1], control[2], (colorI-0.16)/0.26);
    } else if (colorI < 0.6425) {
    color = mix(control[2], control[3], (colorI-0.42)/0.2225);
    } else if (colorI < 0.8575) {
    color = mix(control[3], control[4], (colorI-0.6425)/0.215);
    } else {
    color = mix(control[4], control[0], (colorI-0.8575)/0.1425);
    }
    vec3 sc = vec3(pow(color.x, gamma), pow(color.y, gamma), pow(color.z, gamma));
    vec3 n = normal(k);
    vec3 l = normalize(vec3(3.0,2.0,-10.0)-k);
    float shade = dot(n,l)*softshadow(k,l,0.01,32.);
    vec3 h = normalize(l-r);
    float spec = exp(-acos(max(0.,dot(n,h)))/0.25);
    return min(vec3(1.0,1.0,1.0), shade * sc + vec3(spec));
}
