#define R 1.
#define RPetal 0.3

#define N (14.)

#define leafSz 0.4


#define volSc 0.03
#define stickSz 1.4


bool hit = false;


#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

#define pmod(p,a) mod(p,a) - 0.5*a

#define opRepLim(p,c,l) (p-c*clamp(round(p/c),-l,l))


const float PI = 3.14159265359;
const float PHI = 1.61803398875;
vec4 inverseSF( in vec3 p, const in float n )
{

    float phi = min(atan(p.y,p.x),PI);
    float k   = max(floor(log(n*PI*sqrt(5.0)*(1.-p.z*p.z))/log(PHI+1.)),2.0);
    float Fk  = pow(PHI,k)/sqrt(5.0);
    vec2  F   = vec2(round(Fk),round(Fk*PHI));
    vec2  G   = PI*(fract((F+1.0)*PHI)-(PHI-1.0));    

    mat2 iB = mat2(F.y,-F.x,G.y,-G.x)/(F.y*G.x-F.x*G.y);
    vec2 c = floor(iB*0.5*vec2(phi,n*p.z-n+1.0));

    float ma = 0.0;
    vec4 res = vec4(0);
    for( int s=0; s<4; s++ )
    {
        vec2 uv = vec2(s&1,s>>1);
        float i = dot(F,uv+c);
        float phi = 2.0*PI*fract(i*PHI);
        float cT = 1.0 - (2.0*i+1.0)/n;
        float sT = sqrt(1.0-cT*cT);
        vec3 q = vec3(cos(phi)*sT, sin(phi)*sT,cT);
        float a = dot(p,q);
        if (a > ma)
        {
            ma = a;
            res.xyz = q;
            res.w = i;
        }
    }
    return res;
}



mat3 makeBase( in vec3 w )
{
	float k = inversesqrt(1.0-w.y*w.y);
    return mat3( vec3(-w.z,0.0,w.x)*k, 
                 vec3(-w.x*w.y,1.0-w.y*w.y,-w.y*w.z)*k,
                 w);
}


vec3 sphericalFromCart(vec3 p){
    p.yz *= rot(-0.5*PI);

    return vec3(
        length(p),
        acos(p.z/length(p)),
        atan(p.y,p.x)
    );
}
vec3 cartFromSpherical(vec3 p){
    //p.xy *= rot(0.25*PI);

    return vec3(
        p.x*sin(p.y)*cos(p.z),
        p.x*sin(p.y)*sin(p.z),
        p.x*cos(p.y)
    );
}


vec3 acesFilm(const vec3 x) {
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return clamp((x * (a * x + b)) / (x * (c * x + d ) + e), 0.0, 1.0);
}

vec2 dmin(vec2 a, float b, float id){
    return a.x < b ? a : vec2(b,id);
