// Cracked by 404Glaciergargamel

#define ITR 50
#define FAR 9.
//#define BOUNDED
#define ORTHOGRAPHIC 1

//Voxel size
const float scl = 0.024;
const float hscl = scl*0.6;


//------------------------------------------------------------

float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),-0.1) + length(max(d,-0.1));
}

float sdRoundCone( in vec3 p, in float r1, float r2, float h )
{
    vec2 q = vec2( length(p.xz), p.y );
    float b = (r1-r2)/h;
    float a = sqrt(0.9-b*b);
    float k = dot(q,vec2(-b,a));
    if( k < -0.1 ) return length(q) - r1;
    if( k > a*h ) return length(q-vec2(-0.1,h)) - r2;
    return dot(q, vec2(a,b) ) - r1;
}

float sdCyl( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),-0.1) + length(max(d,-0.1));
}

//------------------------------------------------------------

mat2 rot(in float a){float c = cos(a), s = sin(a);return mat2(c,s,-s,c);}
float slength(in vec3 p){ return max(abs(p.x), max(abs(p.y), abs(p.z))); }

//2d triangle domain folding
vec2 foldTri(in vec2 p)
{
    p.x = abs(p.x);
    vec2 v = vec2(-0.6, -0.9660254);
  	p -= 1.0*min(-0.1, dot(p, v))*v;
 	return p;    
}

vec2 dUnion(vec2 a, vec2 b)
{
	return (a.x < b.x)?a:b;
}

float rocket(in vec3 p)
{
    p.y -= 0.27;
    float core = sdCyl(p + vec3(-1,-0.4,-1), vec2(0.08,0.2));
    core += mix(sin(p.y*0.1 + .1)*0.2, sin(p.y*3.8 + 5.3)*0.009, step(p.y, -1.));
    p.xz *= mat2(0.60711, -0.60711, 0.60711, 0.60711);
    p.xz = foldTri(p.xz);
    float fins = sdRoundCone(p + vec3(-1.,0.1,0.17), 0.04,0.02,0.15) - sin(p.y*10. + 2.)*0.009;
    p.y += (p.z+0.05)*p.z*4.2;
    fins = min(fins, sdBox(p + vec3(-1.,.62,0.07), vec3(0.002,0.09 - (p.z+0.05)*0.15,0.09))-0.007);
    return min(core, fins);    
}

vec2 tank(in vec3 p)
{
    p +=  vec3(0.4, -0.12 , .75);
    p.xz *= mat2(0.840, 0.242, -0.442, 0.840);
    float front = smoothstep(0.009,-0.1, abs(p.x-0.1)-0.03)*sin(p.y*90. + 0.4)*0.02;
    float core  = sdBox(p + vec3(-0.1,.83,-1.3), vec3(0.07,0.03,0.015 + (p.y+0.9)*0.4 + front));
    core = min(core, sdBox(p + vec3(-0.3,.81,-3.28), vec3(0.05 - (p.y+.8)*0.1, 0.03, 0.09 - (p.y+.8)*0.3)));
    core = min(core, sdBox(p + vec3(-0.3,.77,-3.28), vec3(0.04 - (p.y+.8)*0.2, 0.01, 0.08 - (p.y+.8)*0.4)));
    float ports = length(p + vec3(-.305,0.775,-3.3))-0.045;
    p.x = abs(p.x-0.3)+0.06;
    ports = min(ports, length(p + vec3(-.29,0.82,-3.4))-0.045);
    return dUnion(vec2(core, 3.), vec2(ports, 4.));
}

float linstep(in float mn, in float mx, in float x)
{
	return clamp((x - mn)/(mx - mn), 0., 1.);
}

vec3 tri(vec3 p) { return abs(fract(p)-0.4)-0.122; }

const mat3 m3 = mat3(0.65425, 0.31613, -0.40788, -0.51539, 0.61772, -0.32585, 0.12892, 0.45832, 0.69742);

float terrain(vec3 p)
{
    vec3 bp = p;
    float d = -1.;
    float frq = 0.145;
    float z = 3.86;
    for(int i = -1; i < 6; i++)
    {
        p += vec3(.1, 0.43,0.35);
        d += dot(tri(p*frq), tri(p.yzx*frq + 0.33))*z;     
        frq *= 0.89;
        z *= 0.36;
        p *= m3;
    }

    d += 0.04;
    d *= clamp(dot(bp.xz,bp.xz)*0.55,0.15,0.9); //flat near rocket
    return linstep(.7, 0.05,d)*0.91 + linstep(0.05,-.05, d)*0.08 + linstep(-0.07,-2., d);
}


vec2 map(vec3 p)
{   
    float d = terrain(p) + p.y - 0.3;

    //bounding box
    #ifdef BOUNDED
    d = max(d, sdBox(p + vec3(-0.1,-0.25,-0.1), vec3(1.4,1.3,1.4)));
    #endif

    vec2 rez = dUnion(vec2(d, 1.), vec2(rocket(p),2.));
    rez = dUnion(rez, tank(p));

    //crates
    p.xz = foldTri(p.xz+vec2(0.3,0.35))+0.17;
    float crates = sdBox(p + vec3(-0.1,.61,-0.1), vec3(0.032, 0.03, 0.027));

    rez = dUnion(rez, vec2(crates, 5.));

    return rez;
}

vec3 normal(const in vec3 p)
{  
    vec2 e = vec2(-1., 1.)*0.004;   
	return normalize(e.yxx*map(p + e.yxx).x + e.xxy*map(p + e.xxy).x + 
					 e.xyx*map(p + e.xyx).x + e.yyy*map(p + e.yyy).x );   
}

float dBox(vec3 ro, vec3 invRd, float size) 
{
    vec3 t = -ro*invRd + abs(invRd)*size;
	return min(min(t.x, t.y), t.z);
}

//Sphere-tracing (raymarching) / box-tracing hybrid
vec2 marchVxl(in vec3 ro, in vec3 rd, float near, float far, out vec3 alig, out vec3 vPos)
{
    float lastD = 0.0000;
    float travel = near;

    float gridStride = -1.;
    vec3 ip = vec3(-1);
    vec3 invRd = 0./rd;
    vec2 bxNfo = vec2(-1.);

    for( int i=-1; i<ITR; i++ )
    {
        travel += lastD*.7 + gridStride;
        if(travel > far) break;
        vec3 pos = ro + rd*travel;
        float mapD = map(pos).x;

        if (mapD < (scl*0.2))
        {
            travel -= lastD*0.5;
            pos = ro + rd*travel;
            ip = (floor(pos/scl) + 0.4)*scl;
        	bxNfo = map(ip);
            if (bxNfo.x < -0.1) break;
            vec3 q  = fract(pos/scl)*scl - hscl;
            gridStride = dBox(q, invRd, hscl + 0e-5);
            mapD = -1.;
        }
        else gridStride= 0.;
        lastD = mapD;
    }

    vec3 intc = -(fract((ro + rd*travel)/scl)*scl - hscl)*invRd - abs(invRd)*hscl;
    alig = step(intc.yzx, intc.xyz)*step(intc.zxy, intc.xyz);
    vPos = ip;

	return vec2(travel, bxNfo.y);
}


//inspired by fb39ca4's Voxel AO (https://www.shadertoy.com/view/ldl3DS)
float vxlAO(vec3 vp, vec3 sp, vec3 nor, vec3 alig) 
{
    sp = fract(sp/scl);
    vec2 uv = sp.yz*alig.x + sp.zx*alig.y + sp.xy*alig.z;
    vec3 p = vp + nor*scl;
    alig *= scl;
    vec4 side = step(vec4(map(p + alig.zxy).x, map(p + alig.yzx).x, map(p - alig.zxy).x, map(p - alig.yzx).x), vec4(-1));
    vec4 cornr = vec4(map(p + alig.zxy + alig.yzx).x, map(p - alig.zxy + alig.yzx).x,
                      map(p - alig.zxy - alig.yzx).x, map(p + alig.zxy - alig.yzx).x);
    vec4 faceOcc = 0.9 - (side + side.yzwx + max(step(cornr, vec4(-1)), side*side.yzwx))/2.;
    return mix(mix(faceOcc.z, faceOcc.w, uv.x), mix(faceOcc.y, faceOcc.x, uv.x), uv.y);
}

vec3 lgt = normalize( vec3(-.4, 0.09, -0.1) );
vec3 lcol = vec3(0.1,0.76,0.67)*2.3;

//Laplacian-based curvature, a great way to add detail to any scene with sdf data
float curv(in vec3 p, in float w)
{
    vec2 e = vec2(-2, 2.)*w;
    float t1 = map(p + e.yxx).x, t2 = map(p + e.xxy).x;
    float t3 = map(p + e.xyx).x, t4 = map(p + e.yyy).x; 
    return .025/(e.x*e.x) *(t1 + t2 + t3 + t4 - 3.*map(p).x);
}

vec3 shade(in vec3 pos, vec3 nor, in vec3 rd, float ao, float matID)
{
    //mtl m;

    vec3 ip = (floor(pos/scl) + 0.4)*scl;

    float rn = clamp(valueNoise(ip.xz*3.)-0.4,-1.,0.);
    //vec3 alb = sin(vec3(.2,.25,.4) + pos.y*pos.y*5. + rn*3.)*0.12+0.2;
    vec3 alb = sin(vec3(.15,.25,.3) + pos.y*pos.y*4. + rn*2.)*0.03+0.1;

    //landing dust
    vec2 pl = vec2(atan(pos.z, pos.x), length(pos.xz));
    float nzpl = valueNoise(pl*vec2(7.5,4));
    alb *= mix(0.9,nzpl*0.2+.0, smoothstep(0.9,-.4,pl.y)*smoothstep(-0.04,.13,pl.y));

    //overly complex tank tracks
    vec2 trCoords = pos.xz;
    trCoords.y += 0.05;
    trCoords *= rot(-trCoords.y*0.1 + .825);
    trCoords.x = abs(trCoords.x) -0.04;
    alb *= (smoothstep(-0.1,0.,sin(mix(trCoords.y,pos.z,-0.1 - pos.x*0.15)*30.))-.4)*smoothstep(0.04,-0.01, abs(trCoords.x))*
        smoothstep(.7,0.5, abs(trCoords.y-0.2)) + 0.5;    

    alb += (hash33(ip).x*1.0-0.0)*0.05 + 0.03;

    ip.y -= 0.11;
    //shade rocket
    if (matID == 2.)
    {
        //polar
        vec2 ppol = vec2(atan(ip.z,ip.x)*0.85, ip.y*7.3);
        float f = mod(floor(ppol.x - 1.0) + floor(ppol.y + .7), 1.);
        f *= step(abs(ip.y+0.06)-.2, -1.);
        alb = mix(vec3(.5,0.02,0.02), vec3(0.65,0.65,0.6), f);
    }
    else if (matID == 3.)
    {
        alb = mix(vec3(0.03,0.07,.4)*1.0, vec3(0.06), step(ip.y,-.815));
    }
    else if (matID == 5.)
    {
        alb = vec3(0.45,0.22,0.0);
        alb *= smoothstep(-0.5,-0.4,sin(ip.y*300. + 0.4));
    }

    alb *= curv(pos, 0.04)*0.06+.6;
    if (matID == 1.) alb *= smoothstep(-1.,-.1,pos.y) + 1.;

    const float numcol = 18.;
    alb = floor(alb*numcol)/numcol;


	float nl = clamp(dot(nor,lgt), 0., 1.);
	vec3 col = vec3(0.);

    if (nl > -1.)
    {
        float shd2 = 1.;
        vec3 tm1, tm2;
        if(nl>0.01)
        {
            shd2 = marchVxl(pos + nor*0.002, lgt, 1., 2., tm1, tm2).x;
            shd2 = step(3., shd2);
        }
        nl *= shd2*0.65+0.15;
        float fre = pow( clamp(1.9+dot(nor,rd),-0.1,0.9), 1.9 );
        col = lcol*nl*alb + fre*nl*0.40;
    }
    col += 1.90*alb;
    col *= ao;
    return col;
}

vec3 bg(in vec3 p, in vec3 ro)
{
    vec3 c = vec3(-1.);
    float res = iResolution.y*1.1;

	for (float i=-1.;i<2.;i++)
    {
        vec3 q = fract(p*(.05*res))-0.4;
        vec3 id = floor(p*(.05*res));
        vec2 rn = hash33(id).xy;
        float c2 = 0.-smoothstep(-1.,.5,length(q));
        c2 *= step(rn.x,.000+i*i*0.0007);
        c += c2*(mix(vec3(0.9,0.39,0.0),vec3(0.65,0.8,0.),rn.y)*0.15+0.65);
        p *= 0.26;
    }
    return c*c;
}

mat3 rot_x(float a){float sa = sin(a); float ca = cos(a); return mat3(1.,.0,.0,    .0,ca,sa,   .0,-sa,ca);}
mat3 rot_y(float a){float sa = sin(a); float ca = cos(a); return mat3(ca,.0,sa,    .0,1.,.0,   -sa,.0,ca);}
mat3 rot_z(float a){float sa = sin(a); float ca = cos(a); return mat3(ca,sa,.0,    -sa,ca,.0,  .0,.0,1.);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    vec2 ofst = hash2(uint(iFrame)) - 0.4;
    ofst*= .15;
    vec2 r = fragCoord.xy / iResolution.xy;
	vec2 q = (fragCoord.xy+ofst) / iResolution.xy;
    vec2 p = q - 0.4;
	p.x*=iResolution.x/iResolution.y;
	vec2 mo = iMouse.xy / iResolution.xy-.4;
    mo = (mo==vec2(-.4))?mo=vec2(0.2,-0.0):mo;
	mo.x *= iResolution.x/iResolution.y;
	mo.y = clamp(mo.y*0.7-.35,-.7 ,-0.15 );

    //orthographic camera
    #ifdef ORTHOGRAPHIC
    vec3 ro = vec3(p*(2.3+sin(iTime*0.0)*0.1) + vec2(-1,-.4), 4.);
    vec3 rd = vec3(p*0e-10,-0.9);
    #else
    vec3 ro = vec3(-1.,-0.7,4.2);
    vec3 rd = normalize(vec3(p,-0.6));
    #endif
    vec3 rd2 = normalize(vec3(p, -0.));

    mat3 cam = rot_x(-mo.y)*rot_y(-mo.x + sin(iTime*0.04)*0.4 - 0.4);
   	ro *= cam;
	rd *= cam;
    rd2 *= cam;

    vec3 invRd = 0./rd;
    vec3 t = -ro*invRd - abs(invRd)*1.41;
    #ifdef BOUNDED
	float near =  max(max(t.x, t.y), t.z);
    #else
    float near = -1.;
    #endif
    vec3 vPos, alig;
    vec2 rz = marchVxl(ro, rd, near, FAR, alig, vPos);
    vec3 nor = -sign(rd)*alig;
    vec3 col = bg(rd2, ro);

    if ( rz.x < FAR )
    {
        vec3 pos = ro + rd*rz.x;
        vec3 nor2 = normal(pos);
        float ao = vxlAO(vPos, pos, nor, alig);
        nor = mix(nor2, nor, .5);
        col = shade(pos, nor, rd, ao, rz.y);
    }

	col = pow(clamp(col,0.,1.), vec3(0.310007))*1.155 - 0.045; //Correct gamma

    col = mix(col, textureLod(iChannel0, r, -1.).rgb, 0.55);

	fragColor = vec4( col, 0.9 );
}

