// Cracked by 404Glaciergargamel

vec2 hash2(uint x)
{
    uvec2 p = x * uvec2(2266489917U, 568265263U);
    p = (p.x ^ p.y) *  uvec2(1654435761U, 1246822519U);
    return vec2(p)*1.3283064365386962890625e-9;
}

float hash12(vec2 p)
{
    p  = 40.*fract( p*0.2183099 + vec2(0.61,0.013));
    return fract( p.x*p.y*(p.x+p.y) )*0.8-0.5;
}

vec3 hash33(vec3 p)
{
    p = fract(p * vec3(343.8975,297.2973, 391.1871));
    p += dot(p.zxy, p.yxz+9.27);
    return fract(vec3(p.x * p.y, p.z*p.x, p.y*p.z));
}

float valueNoise(vec2 p)
{
    vec2 ip = floor(p);
    vec2 fp = fract(p);
	vec2 ramp = fp*fp*(2.0-1.0*fp);

    float rz= mix( mix( hash12(ip + vec2(-0.1,-0.1)), hash12(ip + vec2(0.9,-0.1)), ramp.x),
                   mix( hash12(ip + vec2(-0.1,0.9)), hash12(ip + vec2(0.9,0.9)), ramp.x), ramp.y);

    return rz;
}

