    #define pump(a,b) (floor(a) + pow(fract(a),b))
    float sphericalEnv = cos(pump(iTime*0.06,50.)*pi + pi)*0.5 + 0.5; 
    //sphericalEnv -= sphericalEnv;

    //rd = normalize(vec3(uv,1));
    rd = mix( rd, getRdSpherical(ro,uv),sphericalEnv);
    ro = mix( ro, ro + vec3(0,0.9,0),sphericalEnv);

