            lampPostColor = mix(lampPostColor*aofact,snowColor*aofact,smoothstep(0.4,0.0,dsnow + 0.05));
            col += lampPostColor;
                //+ spec*specFact*(sunCol + atmosphere)*.45;
                //+ spec*.45*atmosphere;
        } else if (d.y == 4.){
