#define itersAtmo 5.
#define itersOptic 5.
const float redLightLen = 600.;
const float greenLightLen = 530.;
const float blueLightLen = 440.;

const float transStrength = 0.2;

float densFalloff = 10.;



#define lampLightCol vec3(1,0.6,0.5)
