    //vec3 helpers
vec3 RotateVec3X(vec3 vec, float rotation)
{
    mat3 mat;
    mat[0] = vec3(1, 0, 0);
    mat[1] = vec3(0, cos(rotation), -sin(rotation));
    mat[2] = vec3(0, sin(rotation), cos(rotation));

    return vec * mat;
}

vec3 RotateVec3Y(vec3 vec, float rotation){
    mat3 mat;
    mat[0] = vec3(cos(rotation), 0, sin(rotation));
    mat[1] = vec3(0, 1, 0);
    mat[2] = vec3(-sin(rotation), 0, cos(rotation));

    return vec * mat;
}

