    //directions
vec3 CameraDirection()
{
    vec2 mouseUV = vec2(iMouse.x / iResolution.x, iMouse.y / iResolution.y);

    if(mouseUV == vec2(0,0))
    {
        mouseUV = vec2(.5, .5);
    }

    vec2 angle = vec2(-mouseUV.x * PI * 2.0, -mouseUV.y * PI + PI / 2.0);

    vec3 vec = vec3(0,0,1);
    vec = RotateVec3X(vec, angle.y);
    vec = RotateVec3Y(vec, angle.x);

    return vec;
}

vec3 RayDirection(vec3 direction, vec2 UV, float fov)
{
    vec2 signedUV = UV * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    vec2 aspectSignedUV = vec2(signedUV.x, signedUV.y / aspect);
    vec2 offset = aspectSignedUV * tan(fov / 2.0);

    vec3 front = (direction);
    vec3 right = normalize(cross(front, vec3(0,1,0)));
    vec3 up = cross(right, front);

    return normalize(front + right * offset.x + up * offset.y);
}

