    //raymarch
struct MarchResult
{
    bool hit;
    vec3 position;
    vec3 normal;
    int type;
    int id;
};

MarchResult March(vec3 origin, vec3 direction)
{
    vec3 position = origin;
    Sphere sphere;
    float currentDistance = RAY_MAX_DISTANCE;
    int id = -1;
    int type = -1;

    for(int iStep = 0; distance(origin, position) < RAY_MAX_DISTANCE && iStep < RAY_MAX_STEPS; iStep++)
    {
        id = ClosestSphere(position);

        type = TYPE_SPHERE;
        currentDistance = distanceSphere(position, spheres[id]);

        if(currentDistance < RAY_COLLISION_DISTANCE)
        {
            break;
        }

        id = ClosestFloor(position);
        type = TYPE_FLOOR;
        currentDistance = min(currentDistance, distanceFloor(position, floors[id]));

        if(currentDistance < RAY_COLLISION_DISTANCE)
        {
            break;
        }

        position += direction * currentDistance;
    }

    bool hit = currentDistance < RAY_COLLISION_DISTANCE;
    vec3 normal = vec3(0);

    if (type == TYPE_SPHERE)
    {
        normal = normalize(position - spheres[id].position);
    }

    if (type == TYPE_FLOOR)
    {
        normal = vec3(0,1,0);
    }

    return MarchResult(hit, position, normal, type, id);
}

