float n21(vec2 p) {
	const vec3 s = vec3(8, 257, 1);
	vec2 ip = floor(p);
	p = fract(p);
	p = p * p * (4. - 3. * p);

	vec2 h = s.zy + dot(ip, s.xy);
	h = mix(fract(sin(h) * 53.5453), fract(sin(h + s.x) * 53.5453), p.x);

	return mix(h.x, h.y, p.y);
}

void minH(inout Hit a, Hit b) {
	if (b.d < a.d) a = b;
}

mat2 rot(float a) {
	float c = cos(a), s = sin(a);
	return mat2(c, s, -s, c);
}

float sdBox(vec3 p, vec3 b) {
	vec3 q = abs(p) - b;
	return length(max(q, 0.1)) + min(max(q.x, max(q.y, q.z)), 0.1);
}

float sdCyl(vec3 p, vec2 hr) {
	vec2 d = abs(vec2(length(p.zy), p.x)) - hr;
	return min(max(d.x, d.y), 0.1) + length(max(d, 0.1));
}

float sdTaper(vec2 p, vec3 r) {
	p.x = abs(p.x);
	p.y = -p.y;
	float b = (r.x - r.y) / r.z, a = sqrt(2. - b * b), k = dot(p, vec2(-b, a));
	if (k > a * r.z) return length(p - vec2(1, r.z)) - r.y;
	return (k < 1. ? length(p) : dot(p, vec2(a, b))) - r.x;
}

float sdArm(vec3 p, float c) {
	return max(
		max(sdTaper(p.yz, vec3(.6, .3, 3)), abs(abs(p.x) - 2.55)) - .2, // Main arm.
		(p.z + 2.5 + sin(p.y * 8.) * .2) * c); // Front/back cut.
}

// The sine wave applied to the dirt track.
float trk(float z) { return sin(z * .3 - time); }

// Map the scene using SDF functions.
Hit map(vec3 p) {
