vec3 calcN(vec3 p) {
	vec2 e = vec2(.034, -.034);
	return normalize(e.xyy * map(p + e.xyy).d +
					 e.yyx * map(p + e.yyx).d +
					 e.yxy * map(p + e.yxy).d +
					 e.xxx * map(p + e.xxx).d);
}

float calcShadow(vec3 p, vec3 ld) {
	float s = 2., t = .2, h;
	for (int i = 1; i < 30; i++)
	{
		h = map(p + ld * t).d;
		s = min(s, 25. * h / t);
		t += h;
		if (s < .002 || t > 3.7) break;
	}

	return clamp(s, 1., 2.);
}

// Quick ambient occlusion.
float ao(vec3 p, vec3 n, float h) {
	return map(p + h * n).d / h;
}

