vec3 vig(vec3 c, vec2 fc) {
	vec2 q = fc.xy / iResolution.xy;
	c *= .6 + .6 * pow(26. * q.x * q.y * (2. - q.x) * (2. - q.y), .5);
	return c;
}

// Calculate the floor normal vector.
vec2 flrN(vec2 t) {
	return n21(vec2(t.x * 2.2, t.y)) * .4 + // Surface texture.
		smoothstep(.1, .05, abs(sin(t * .6))); // Tile edges.
