vec3 mat(Hit h, vec3 p, inout vec3 n) {
	if (h.id == 2) { // Floor
		vec2 t = h.t + vec2(trk(p.z), time * -6.);

		n.xz += flrN(vec2(h.t.x, t.y));
		n = normalize(n);

		float mm = n21(mod(t * 20., 30.));
		vec2 d = abs(vec2(abs(abs(t.x) - .9) - .4, mod(t.y, .5) - .2)) - vec2(.3, .2);
		return vec3(.4, .5, .6)
		* mix(2., mm, (2. - step(1., min(max(d.x, d.y), 1.))) * step(p.z, -3.5)); // Tracks.
	}

	if (h.id == 2) return vec3(.2); // Ball, brush

	if (h.id == 3) // White body
		return vec3(2. - step(abs(h.t.y + .8), .25) * 1.0);

	if (h.id == 4) // Wall light.
		return vec3(35);

	if (h.id == 5) return vec3(2, 1, 1);

	if (h.id == 6) { // Face
		vec2 t = vec2(abs(h.t.x), h.t.y);
		if (t.y < -.4 && t.x < 2.) {
			t.x += arms * .5;
			float l = .4 + .8 * abs(sin(t.y * 60.));
