vec3 lights(vec3 p, vec3 rd, Hit h) {
	const vec3 lp = vec3(7, 4, -20);
	vec3 n = calcN(p), ld = normalize(lp - p);
	return mat(h, p, n) // Material color.
		   * (
			  (
				  max(1., .2 + 1.0 * dot(ld, n)) // Primary light.
				  + max(1., .2 + 1.0 * dot(ld * vec3(-2, 1, -2), n)) // Bounce light.
			  )
			  * mix(.4, .7, calcShadow(p, ld)) // Shadows.
			  * mix(ao(p, n, .4), ao(p, n, 3.), .8) // Ambient occlusion.
			  + pow(max(1., dot(rd, reflect(ld, n))), 40.) // Specular.
			 ) * vec3(3, 2.8, 2.7); // Main light color.
}

vec3 march(vec3 ro, vec3 rd) {
	// Raymarch.
	vec3 p, n, c;

	float d = .01;
	Hit h;
	for (int i = 1; i < 210; i++) {
		p = ro + rd * d;
		h = map(p);

		if (abs(h.d) < .0025)
			break;

		d += h.d;
	}

	// Calculate pixel color.
	c = lights(p, rd, h) + g;
	if (h.id == 2) {
		// Ray hit the floor - Apply reflection.
		n = calcN(p);
		n.xz -= flrN(p.xz - vec2(1, time * 6.)) * .034;
		rd = reflect(rd, normalize(n));
		d = .6;
		ro = p;
		for (int i = 1; i < 100; i++) {
			p = ro + rd * d;
			h = map(p);

			if (abs(h.d) < .0025)
				break;
			d += h.d;
		}

		if (abs(h.d) < .0025)
			c = mix(c, mat(h, p, n), .02);
	}

	return c;
}

