            //C = T( pPtr.xy );

            vec4 Cold = C;
            float velScale = 1.;

            findNeighbors( C, vec2(1,0));
            findNeighbors( C, vec2(-1,0));
            findNeighbors( C, vec2(0,1));
            findNeighbors( C, vec2(0,-1));

            findNeighbors( C, vec2(1,1));
            findNeighbors( C, vec2(1,-1));
            findNeighbors( C, vec2(-1,1));
            findNeighbors( C, vec2(-1,-1));



            for(int i = 0; i < neighborsCnt; i++){
                //vec2 idx = getIdx(i);
                //if(idx.x > U.x+0. && idx.x < U.x -0.  ){


                vec4 otherPtr = neighborPtrs[i];
                //vec4 other = TF1(otherP.xy*R);
                vec4 other = T(otherPtr.xy);

                vec2 meToOther = (other.xy - C.xy)*resFact.xy;


                float distBetween = length(meToOther);

                if( distBetween < particleSz * 2.0 ){

                    vec2 vel = C.zw;
                    vec2 otherVel = other.zw;

                    vec2 normal = normalize(meToOther);
                    vec2 tangent = vec2(-normal.y,normal.x);


                    float meNorm = dot(vel, normal);
                    float meTan = dot(vel, tangent);
                    float otherNorm = dot(otherVel, normal);
                    float otherTan = dot(otherVel, tangent);

                    float massMe = 1.;
                    float massOther = 1.;

                    massMe = pPtr.z*1.;
                    massOther = otherPtr.z*1.;

                    meNorm = ( meNorm * (massMe - massOther) + 2.*massOther*otherNorm ) / (massMe + massOther);



                    if ( false && pPtr.z == otherPtr.z && pPtr.z == 1.){
                       C.zw = mix(C.zw, otherVel,0.01);

                    } else {
                        C.zw = tangent * meTan + normal*meNorm;

                    }
                    C.xy -= normalize(meToOther)*(abs(distBetween - particleSz*2.));

                    //otherNorm = (otherNorm(massOther - massMe) + 2.*massOther*otherNorm ) / (massMe + massOther);


                }


            }

            if (abs(C.x - 0.5) > 0.49 - particleSz*0.5){
                C.z *= -1.;
                if ( sign(C.z) != sign(-C.x + 0.5) )
                    C.z *= -1.;
            }
            if (abs(C.y - 0.5) > 0.49 - particleSz*0.5){
                C.w *= -1.;
                if ( sign(C.w) != sign(-C.y + 0.5) )
                    C.w *= -1.;            
            }


            //C.zw = mix(C.zw, ( valueNoise(vec3(C.xy*7.,1. + iTime*0.7),1.).xy*2. - 1. )*particleSpeed, 0.002);


            C.xy += C.zw*1.*resFact.yx;

        }
    }
}

