#define getIdx(a) vec2(mod(a,R.x), floor((a+R.x)/R.x)-1.)


vec4 r24(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);

}


float hash13(vec3 p3){
    p3 = fract((p3)*0.1031);
    p3 += dot(p3, p3.yzx  + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}
///  2 out, 3 in...
vec2 hash23(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

vec2 valueNoise(vec3 uv,float pw){
    vec3 id = floor(uv);
    vec3 fd = fract(uv);
    fd = smoothstep(0.,1., fd);

    fd = pow(fd,vec3(pw));

    vec2 ibl = hash23(id + vec3(0,-1,0));
    vec2 ibr = hash23(id + vec3(1,-1,0));
    vec2 itl = hash23(id + vec3(0));
    vec2 itr = hash23(id + vec3(1,0,0));


    vec2 jbl = hash23(id + vec3(0,-1,1));
    vec2 jbr = hash23(id + vec3(1,-1,1));
    vec2 jtl = hash23(id + vec3(0,0, 1));
    vec2 jtr = hash23(id + vec3(1,0, 1));


    vec2 ibot = mix(ibl, ibr, fd.x); 
    vec2 iup = mix(itl, itr, fd.x);
    vec2 jbot = mix(jbl, jbr, fd.x);
    vec2 jup = mix(jtl, jtr, fd.x);

    vec2 i = mix(ibot, iup, fd.y);
    vec2 j = mix(jbot, jup, fd.y);

    return mix(i, j, fd.z); 
}

