void findMe( inout vec4 pPntr, inout vec4 p, vec2 U, vec2 dir){
    for(float i = 1.; i < steps; i++){
        vec4 otherPntr = T1(U + dir*i);
        vec4 otherP = T(otherPntr.xy);

        if (length( (U/R - otherP.xy)/resFact.yx ) < length( (U/R - p.xy)/resFact.yx )){
            p = otherP;
            pPntr = otherPntr;
        }

    }

}


