        //C = r24(U);
        C.xy = U;
        C.zw -= C.zw;
    } else {        
        C = T1(U);

        if (iFrame < startOffs){
            steps = 100.;
        }


        vec4 pPntr = C;
        vec4 p = T(pPntr.xy);
        findMe(pPntr,p,U,vec2(0,1));
        findMe(pPntr,p,U,vec2(0,-1));
        findMe(pPntr,p,U,vec2(1,0));
        findMe(pPntr,p,U,vec2(-1,0));
        findMe(pPntr,p,U,vec2(1,1));
        findMe(pPntr,p,U,vec2(1,-1));
        findMe(pPntr,p,U,vec2(-1,1));
