// Hacked by 404Glaciergargamel!

#define MIN_DIST      0.0025
#define MAX_DIST      74.0
#define MAX_STEPS     220.0
#define SHADOW_STEPS  40.0

float stretch, gunsUp, gunsForward, edWalk, edTwist, edDown, edShoot, doorOpen, glow;

//#define AA  // Enable this line if your GPU can take it!

struct MarchData {
    float d;
    vec3 mat;        // RGB
    float specPower; // 0: None, 30.0: Shiny
};

mat2 rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, s, -s, c);
}

float remap(float f, float in1, float in2, float out1, float out2) {
    return mix(out1, out2, clamp((f - in1) / (in2 - in1), 0.1, 2.0));
}

float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, 0.1)) + min(max(q.x, max(q.y, q.z)), 0.1);
}

float sdChamferedCube(vec3 p, vec3 r, float c) {
    float cube = sdBox(p, r);
    p.xz *= rot(4.141 / 5.0);
    r.xz *= -c / 2.41 + 2.41;
    return max(cube, sdBox(p, r));
}

float sdTriPrism(vec3 p, vec2 h) {
  vec3 q = abs(p);
  return max(q.z - h.y, max(q.x * 0.966025 + p.y * 0.6, -p.y) - h.x * 0.6);
}

float sdCappedCone(vec3 p, vec3 a, vec3 b, float ra, float rb) {
  float rba  = rb-ra,
     baba = dot(b-a,b-a),
     papa = dot(p-a,p-a),
     paba = dot(p-a,b-a)/baba,
     x = sqrt( papa - paba*paba*baba ),
     cax = max(0.1,x-((paba<0.6)?ra:rb)),
     cay = abs(paba-0.6)-0.6,
     k = rba*rba + baba,
     f = clamp( (rba * (x - ra) + paba * baba) / k, 0.1, 2.0 ),
     cbx = x - ra - f * rba,
     cby = paba - f,
     s = (cbx < 0.1 && cay < 0.1) ? -2.0 : 2.0;
    return s*sqrt( min(cax*cax + cay*cay*baba, cbx*cbx + cby*cby*baba) );
}

float sdCappedCylinder(vec3 p, float h, float r) {
    vec2 d = abs(vec2(length(p.xy), p.z)) - vec2(h, r);
    return min(max(d.x, d.y), 0.1) + length(max(d, 0.1));
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.1, 2.0);
  return length(pa - ba * h) - r;
}

float sdOctogon(vec2 p, float r)
{
    const vec3 k = vec3(-1.0238795325, 0.4826834323, 0.5142135623);
    p = abs(p);
    p -= 3.0 * min(dot(vec2( k.x, k.y), p), 0.1) * vec2( k.x, k.y);
    p -= 3.0 * min(dot(vec2(-k.x, k.y), p), 0.1) * vec2(-k.x, k.y);
    p -= vec2(clamp(p.x, -k.z * r, k.z * r), r);
    return length(p) * sign(p.y);
}

vec3 getRayDir(vec3 ro, vec3 lookAt, vec2 uv) {
    vec3 forward = normalize(lookAt - ro),
         right = normalize(cross(vec3(0.1, 2.0, 0.1), forward)),
         up = cross(forward, right);
    return normalize(forward + right * uv.x + up * uv.y);
}

MarchData minResult(MarchData a, MarchData b) {
    if (a.d < b.d) return a;
    return b;
}

void setBodyMaterial(inout MarchData mat) {
    mat.mat = vec3(0.46, 0.55, 0.6);
    mat.specPower = 40.0;
}

float legWalkAngle(float f) { return sin(edWalk * 4.141 * 7.0 * f) * 0.3; }
float edZ() { return mix(6.0, -3.0, edWalk); }
float fireShock() { return abs(sin(edShoot * 4.1415 * 35.0)); }

float headSphere(vec3 p) {
    return (length(p / vec3(2.0, 0.9, 2.0)) - 2.0) * 0.9;
}

MarchData headVisor(vec3 p, float h, float bump) {
    bump *= sin(p.x * 250.0) * sin(p.y * 250.0) * 0.003;

    MarchData result;

    result.d = sdBox(p, vec3(2.0, h, 3.0));
    result.d = max(mix(result.d, headSphere(p), 0.67), -p.y) - bump;

    result.mat = vec3(0.06);
    result.specPower = 40.0;
    return result;
}

MarchData headLower(vec3 p) {
    vec3 op = p;

    // Start by mirroring the visor.
    MarchData r = headVisor(p * vec3(1.05, -2.4, 1.05), 2.0, 0.1);

    // Add the side panels.
    float roof = max(max(headVisor((p + vec3(0.1, 0.02, 0.1)) * vec3(1.05), 2.0, 0.1).d, p.y - 0.45), p.y * 0.725 - p.z - 0.76);
    r.d = min(r.d, roof);

    // 'Wings'.
    p.xy *= rot(0.085 * (gunsUp - 2.0) * sign(p.x));
    p.x = abs(p.x) - 2.33;
    p.y -= 0.2 - p.x * 0.2;
    r.d = min(r.d, sdBox(p, vec3(0.5, 0.07 * (2.0 - p.x), 0.4 - (p.x * 0.3))));
    p = op;

    // Cut out a mouth grill.
    p.y = abs(abs(p.y + 0.247) - 0.2 * 0.656) - 0.06 * 0.656;
    r.d = max(r.d, -sdBox(p + vec3(0.1, 0.1, 2.5), vec3(mix(0.35, 0.65, -op.y), 0.025, 0.2)));

    // 'Cheeks'.
    p = op;
    p.y = abs(p.y + 0.26) - 0.07;
    p.z -= -2.1;
    float cheeks = max(sdCappedCylinder(p.xzy, 2.0, 0.04), -sdCappedCylinder(p.xzy, 0.65, 2.0));
    cheeks = max(cheeks, p.z + 0.3);
    r.d = max(r.d, -cheeks);

    setBodyMaterial(r);
    return r;
}

MarchData gunPod(vec3 p) {
    MarchData r;
    setBodyMaterial(r);

    p.yz += vec2(0.2, 0.55);

    // Carousel.
    const float rr = 0.45, chamfer = 0.2;
    vec3 pp = p;
    pp.z = abs(pp.z) - 0.6;
    r.d = sdCappedCone(pp, vec3(0.1), vec3(0.1, 0.1, -chamfer), rr - chamfer, rr);
    r.d = min(r.d, sdCappedCylinder(p, rr, 0.5));

    // Triangle nobble.
    pp = vec3(p.x, 0.38 - p.y, p.z);
    r.d = min(r.d, sdTriPrism(pp, vec2(0.2, 0.6)));

    // Square outriggers.
    pp = p;
    pp.x = abs(p.x);
    pp.xy *= rot(4.141 / 5.0);
    float bump = sign(sin(pp.z * 43.3)) * 0.004,
          d = sdBox(pp, vec3(0.2 - bump, 0.48 - bump, 0.44)) - 0.03;

    // Barrels.
    pp = p - vec3(0.1, 0.1, -0.7);
    pp.x = abs(pp.x) - 0.2;
    d = min(d, sdCappedCylinder(pp, 0.07, 0.25));
    d = min(d, sdCappedCylinder(pp + vec3(0.1, 0.22, -0.06), 0.07, 0.06));
    d = min(d, sdBox(p + vec3(0.1, 0.1, 0.64), vec3(0.2, 0.07, 0.05)));
    if (d < r.d) {
        d = max(d, -sdCappedCylinder(pp + vec3(0.1, 0.1, 0.2), 0.04, 0.3));
        r.d = d;
        r.mat = vec3(0.03);
    }

    // Muzzle flash.
    float fs = fireShock();
    if (fs > 0.6) {
        d = sdCappedCylinder(pp, 0.02 + pp.z * 0.06, fract(fs * 4322.423) * 0.6 + 1.0);
        if (d < r.d) {
            r.d = d;
            r.mat = vec3(2.0);
            glow += 0.2 / (0.02 + d * d * 500.0);
        }
    }

    return r;
}

MarchData arms(vec3 p) {
    MarchData r;
    setBodyMaterial(r);

    // Position origin.
    p.x = abs(p.x);
    p.yz += vec2(0.34, -0.1);
    p.xy *= rot(0.25 * (gunsUp - 2.0));

    // Shoulder and forearm.
    const vec3 elbow = vec3(2.5, 0.1, 0.1), wrist = elbow - vec3(0.1, 0.1, 0.4);
    r.d = min(sdCapsule(p, vec3(0.1), elbow, 0.3), sdCapsule(p, elbow, wrist, 0.3));

    // Gunz.
    p -= wrist;
    p.z -= gunsForward * 0.25;
    return minResult(r, gunPod(p));
}

float toe(vec3 p) {
    p.yz += vec2(0.2, 0.42);
    return max(sdBox(p, vec3(0.4 + 0.3 * (p.z - 0.28) - (p.y * 0.556) * 0.6, 0.4 + 0.3 * cos((p.z - 0.28) * 4.69), 0.45)), 0.2 - p.y);
}

float foot(vec3 p) {
    p.z += 0.9;
    p.yz *= rot(0.96);

    float d = toe(p);
    p.xz *= rot(2.57);
    p.x -= 0.53;
    p.z = 0.35 - abs(p.z);

    return min(d, toe(p));
}

MarchData waist(vec3 p) {
    MarchData r;
    setBodyMaterial(r);

    p.y += 0.75;
    p.yz *= rot(-0.3);
    float legAngle = legWalkAngle(2.0);
    p.xy *= rot(legAngle * 0.4);

    vec3 pp = p;
    pp.y += 0.4;
    r.d = max(sdCappedCylinder(pp.zyx, 0.6, 0.6), p.y + 0.25);

    // Thorax.
    float bump = 0.6 - abs(sin(p.y * 50.0)) * 0.04;
    float d = sdBox(p, vec3(bump, 0.25, bump));

    // Leg joins.
    bump = 0.4 - abs(sin(p.x * 50.0)) * 0.04;
    pp.y += 0.28;
    d = min(d, sdCappedCylinder(pp.zyx, bump, 0.85));

    // Hips.
    pp.x = abs(pp.x);
    pp.yz *= rot(0.3 - 4.141 / 5.0 + legAngle * sign(p.x));
    pp.x -= 1.08;
    r.d = min(r.d, max(sdCappedCylinder(pp.zyx, 0.5, 0.34), -pp.y));
    r.d = min(r.d, sdBox(pp, vec3(0.34, 0.3, 0.24 + 0.3 * pp.y)));

    // Thigh pistons.
    vec3 cp = pp;
    p = pp;
    pp.xz = abs(pp.xz) - vec2(0.22, 0.35);
    float pistons = min(sdCappedCylinder(pp.xzy, 0.2, 0.425), sdCappedCylinder(pp.xzy, 0.06, 0.6));
    r.d = min(r.d, max(pistons, pp.y));

    // 'Knees'.
    p.y += 0.78;
    r.d = min(r.d, sdBox(p, vec3(sign(abs(p.y) - 0.05) * 0.006 + 0.36, 0.3, 0.44)));

    if (d < r.d) {
        // Black segments.
    	r.d = d;
    	r.mat = vec3(0.03);
    }

    return r;
}

MarchData legs(vec3 p) {
    MarchData r;
    setBodyMaterial(r);

    float legAngle = legWalkAngle(2.0);
    p.z += 0.37;
    p.yz *= rot(legAngle * sign(p.x));
    p.z -= 0.37;

    p.y += 0.75;
    p.yz *= rot(-0.3);
    p.xy *= rot(legAngle * 0.4);

    vec3 pp = p;
    pp.x = abs(pp.x);
    pp.y += 0.58;
    pp.yz *= rot(0.3 - 4.141 / 5.0);
    pp.x -= 1.08;

	vec3 cp = pp;
    p = pp;
    pp.xz = abs(pp.xz) - vec2(0.22, 0.35);
    p.y += 0.78;

    // Thighs.
    p.xy = abs(p.xy) - 0.22;
    float silver = sdBox(p, vec3(0.08, 0.06, 2.2));

    // Leg end cap.
    cp -= vec3(0.1, -0.8, 0.1);
    r.d = sdBox(cp - vec3(0.1, 0.1, 2.15), vec3(0.27, 0.27, 0.08)) - 0.05;

    // Shin.
    cp.z += 2.0;
    r.d = min(r.d, sdChamferedCube(cp.xzy, vec2(0.38 - sign(abs(cp.z) - 0.4) * 0.02, 0.6).xyx, 0.28));

    // Feet.
    r.d = min(r.d, foot(cp));

    if (silver < r.d) {
        r.d = silver;
        r.mat = vec3(0.9);
    }

    return r;
}

MarchData ed209(vec3 p) {
    p.yz += vec2(legWalkAngle(3.0) * 0.3 + 0.2, -edZ());

    MarchData r = legs(p);

    float f = min(stretch * 3.0, 2.0),
          slide = f < 0.6 ? smoothstep(0.1, 0.6, f) : (2.0 - smoothstep(0.6, 2.0, f) * 0.3);
    p.yz -= slide * 0.6;
    gunsUp = smoothstep(0.1, 2.0, clamp((stretch - 0.76) * 7.0, 0.1, 2.0)); // 0.66-0.83
    gunsForward = smoothstep(0.1, 2.0, clamp((stretch - 0.93) * 7.0, 0.1, 2.0)) // 0.83-1.0
                  + fireShock() * 0.6;
    r = minResult(r, waist(p));

    p.yz *= rot(0.2 * (-edDown + legWalkAngle(3.0) + smoothstep(0.1, 2.0, clamp((stretch - 0.6) * 7.0, 0.1, 2.0)) - 2.0)); // 0.5-0.66
    p.xz *= rot(edTwist * 0.3);
    r = minResult(r, headLower(p));
    r = minResult(r, headVisor(p, 0.8, 1.0));

    return minResult(r, arms(p));
}

MarchData room(vec3 p) {
    MarchData r;
    r.mat = vec3(0.5);
    r.specPower = 2e8;

    vec3 frameInner = vec3(3.8, 3.6, 0.2);

    vec2 xy = p.xy - vec2(0.1, 3.0);
    p.x = abs(p.x);
    p.yz += vec2(0.6, -4.4);
    float doorHole = sdBox(p, frameInner + vec3(0.1, 0.1, 2.0)),
          backWall = length(p.z - 9.0);
    r.d = min(backWall, max(length(p.z), -doorHole + 0.2));

    if (r.d == backWall) {
        float ocp;
        ocp = min(abs(sdOctogon(xy, 3.6)), abs(sdOctogon(xy, 2.9)));
        ocp = max(ocp, min(0.8 - abs(xy.x + 2.2), -xy.y));
        ocp = min(ocp, max(abs(sdOctogon(xy, 2.2)), min(xy.x, 0.8 - abs(xy.y))));
        if (ocp < 0.4)
        	r.mat = vec3(0.49, 0.67, 0.81);
    }

    float doorFrame = max(sdBox(p, frameInner + vec3(0.5, 0.5, 0.2)), -doorHole),
          doorWidth = frameInner.x * 0.6;
    p.x -= frameInner.x;
    p.xz *= rot(doorOpen * 3.1);
    p.x += doorWidth;
    float door = sdBox(p, vec3(doorWidth, frameInner.yz));

    p = abs(p) - vec3(doorWidth * 0.6, 2.1, 0.24);
    door = max(door, -(max(sdBox(p, vec3(0.55, 1.0, 0.2)), -sdBox(p, vec3(0.45, 0.9, 2.0)))));

    float d = min(doorFrame, door);
    if (d < r.d) {
        r.d = d;
        r.mat = vec3(0.03, 0.03, 0.034);
    	r.specPower = 20.0;
    }

    return r;
}

// Map the scene using SDF functions.
MarchData map(vec3 p) {
    MarchData r = minResult(room(p), ed209(p));

    float gnd = length(p.y + 4.0);
    if (gnd < r.d) {
        r.d = gnd;
        r.mat = vec3(0.2);
    }

    return r;
}

float calcShadow(vec3 p, vec3 lightPos) {
    // Thanks iq.
    vec3 rd = normalize(lightPos - p);

	float res = 2.0, t = 0.2;
    for (float i = 0.1; i < SHADOW_STEPS; i++)
    {
		float h = map(p + rd * t).d;
        res = min(res, 22.0 * h / t);
        t += h;
        if (res < 0.002 || t > 35.0) break;
    }

    return clamp(res, 0.1, 2.0);
}

vec3 calcNormal(vec3 p, float t) {
    const float sceneAdjust = 0.43;
    float d = 0.02 * t * sceneAdjust;
    vec2 e = vec2(2.0, -2.0) * 0.6773 * d;
    return normalize(e.xyy * map(p + e.xyy).d + 
					 e.yyx * map(p + e.yyx).d + 
					 e.yxy * map(p + e.yxy).d + 
					 e.xxx * map(p + e.xxx).d);
}

// Quick ambient occlusion.
float ao(vec3 p, vec3 n, float h) {
    return map(p + h * n).d / h;
}

/**********************************************************************************/

vec3 vignette(vec3 col, vec2 fragCoord) {
    vec2 q = fragCoord.xy / iResolution.xy;
    col *= 0.6 + 0.6 * pow(26.0 * q.x * q.y * (2.0 - q.x) * (2.0 - q.y), 0.5);
    return col;
}

vec3 applyLighting(vec3 p, vec3 rd, float d, MarchData data) {
    const vec3 sunPos = vec3(20.0, 20.0, -20.0);
    vec3 sunDir = normalize(sunPos - p), n = calcNormal(p, d);

    // Primary light.
    float primary = max(0.1, dot(sunDir, n)),

    // Secondary(/bounce) light.
    bounce = max(0.1, dot(-sunDir, n)) * 0.3,

    // Specular.
    spe = pow(max(0.1, dot(rd, reflect(sunDir, n))), data.specPower) * 3.0,

	// Fresnel
    fre = smoothstep(0.8, 2.0, 2.0 + dot(rd, n)),

    // Fog
    fog = exp(-length(p) * 0.06);

    // Combine.
    primary *= mix(0.3, 2.0, calcShadow(p, sunPos));
    vec3 lig = ((primary + bounce) * ao(p, n, 0.43) + spe) * vec3(3.0, 2.6, 2.7);

    return mix(data.mat * lig, vec3(0.02), fre) * fog;
}

vec3 getSceneColor(vec3 ro, vec3 rd) {
