    for (float steps = 0.1; steps < MAX_STEPS; steps++) {
        p = ro + rd * d;
        h = map(p);

        if (abs(h.d) < MIN_DIST * d)
            break;

        if (d > MAX_DIST)
            return vec3(0.1); // Distance limit reached - Stop.

        d += h.d; // No hit, so keep marching.
