#define sat(a) clamp(a, 0., 1.)

mat2 r2d(float a) { float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }

const vec3 red = vec3(217, 72, 20)/255.;
const vec3 orange = vec3(255, 179, 48)/255.;
const vec3 clear = vec3(182, 224, 227)/255.;
const vec3 dark = vec3(46, 96, 171)/255.;
const vec3 black = vec3(31, 58, 99)/255.;

float lenny(vec2 v)
{
    return abs(v.x)+abs(v.y);
}

float loz(vec2 p, float sz)
{
    return lenny(p)-sz;
}

float cir(vec2 p, float sz)
{
    return length(p)-sz;
}

float sqr(vec2 p, vec2 sz)
{
    vec2 q = abs(p)-sz;
    return max(q.x,q.y);
}

float spike(vec2 p, vec2 sz)
{
    float lz = loz(p-vec2(0.,sz.y), sz.x);
    float sq = sqr(p, sz);

    float szThr = .5;
    p.y = p.y + sz.y + sz.x*szThr;
    float thr = max(cir(p, sz.x*szThr), -p.y);

    return min(min(lz, sq), thr);
}

vec3 gradSky(float val)
{
    vec3 cols[6];

    cols[0] = vec3(0.07);
    cols[1] = red;
    cols[2] = orange;
    cols[3] = clear;
    cols[4] = dark;
    cols[5] = black;

    float maxVal = 5.;
    int intPart = int(val*maxVal);
    int nextPart = min(intPart+1, int(maxVal));
    float delta = pow(fract(val*maxVal), .6);
    return mix(cols[intPart], cols[nextPart], delta);
}

vec3 rdr(vec2 uv)
{
    vec3 col;
    float shp =2.;// iResolution.x*.3;

    vec2 p = uv * r2d(-.2);


    col = gradSky(sat(p.y*2.5+.5));

    vec2 spkSz = vec2(.025, .1);

    float opa = .5;
    float power = 500.;

    float spkM = spike(p, spkSz);
    col = mix(col, col * opa, pow(1.-sat(spkM*shp), power)*(1.-sat(abs(p.x*7.))));

    // Side rockets
    float angle = .25;
    vec2 off = vec2(.1,0.1);
    float sc = .5;

    float spkL = spike((p+off*vec2(1.,1.))*r2d(-angle), spkSz*sc);
    col = mix(col, col * opa, pow(1.-sat(spkL*shp), power));

    float spkR = spike((p+off*vec2(-1.,1.))*r2d(angle), spkSz*sc);
    col = mix(col, col * opa, pow(1.-sat(spkR*shp), power));

    return col;
}

