vec3 rotateY(vec3 p, float k) {
    float c = cos(k);
    float s = sin(k);
    return mat3(c,  0, -s,
                0,  1,  0,
                s,  0,  c) * p;
}

vec3 twistY(vec3 p, float k) {
    float c = cos(k * p.y);
    float s = sin(k * p.y);
    return vec3(mat2(c, -s, s, c) * p.xz, p.y);
}

vec3 calcBoxPos() {
    float y = 1. - 4. * abs(round(iTime / 12.) - iTime / 12.);
    vec3 origin = vec3(sin(iTime / 0.3), y, cos(iTime / 0.3));
    origin.xz *= cos(y * 3.14 / 2.);
    return origin;
}

float calcDist(vec3 p) {
    float dBox = sdBox(twistY(1.2 * (p + 3. * calcBoxPos()), 6.)) - 0.4;
    float dSphere = sdTorus(0.3 * twistY(rotateY(p, iTime), 8.));

    float morphK = 1.8;
    return -log(exp(-morphK * dSphere) + exp(-morphK * dBox)) / morphK;
