    uv = ifs(uv, s, r, int(t));

    fragColor.a = 1.0;
	renderGrid(uv, fragColor.rgb);
    renderUnitSquare(uv, fragColor.rgb);
    renderAxes(vec2(0), uv, fragColor.rgb);

    float pixelSize = 1. / iResolution.y;
    vec3 col = vec3(1) * smoothstep(0.5, 0.5-10.*pixelSize, length(uv))*.25 + .75;
    fragColor.rgb *= col;
}

