// https://darkeclipz.blogspot.com/2020/12/iterated-function-systems-ifs.html 

#define PI 3.14159265358979323846264
#define MOUSE_DOWN (iMouse.z > 0.)
#define SS 4.
#define R iResolution.xy

float random (in vec2 st) {
    return fract(sin(dot(st.xy, vec2(12454.1,78345.2))) * 43758.5);
}

vec2 random2(in vec2 st) {
	return vec2(random(st), random(st));    
}

vec2 ifs(vec2 p, float s, float r, int n) {
    float co = cos(r), si = sin(r);
    mat2 rot = mat2(co, si, -si, co);
    for(int i=0; i < n; i++) {
        p.x = abs(p.x);
        p -= vec2(1.0, 0);
        p *= rot;
        p *= s;
    }
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);
    float px = 1. / iResolution.y;
    for(float i=0.; i < SS; i++) {
        vec2 uv = 5.5 * (2.*(fragCoord + random2(R+i)) - R) / R.y;
        float s = MOUSE_DOWN ? 1.11 + (iMouse.y / iResolution.y - 0.5) * 0.1
                             : 1.11;
        float r = MOUSE_DOWN ? PI / 2. + iMouse.x / iResolution.x * 2. * PI
                             : 2.*PI*fract((iTime + 20.2*2.)/100.);
        uv = ifs(uv, s, r, 24);
        uv = ifs(uv, 1.17, PI/2., 8);
        float ds = 0.5;
        col += vec3(1) * smoothstep(ds, ds-16.*px, length(uv));
    }
    fragColor = vec4(col / SS, 1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// More Rosettes, Matthew Arcus, mla, 2020
//
// Draw rosette pattern looplessly, but also drawing each circle in the same
// colour. Also clip pattern at some radius.
//
// 'c': clip pattern
// 'd': moving dots
//
////////////////////////////////////////////////////////////////////////////////

float X = sqrt(3.0);

float pwidth;

vec3 docircle(vec3 col, vec2 p, vec2 index, vec2 s, vec2 cindex) {
  index += s*cindex;
  uint h = ihash(ivec2(index));
  vec3 ccol = hsv2rgb(float(h)/pow(2.0,32.0),1.0,1.0);
  vec2 ccentre = 0.5*vec2(1,X)*cindex;
  col = mix(ccol,col,0.2+0.8*smoothstep(0.015,0.025+pwidth,circle(p,vec3(ccentre,1))));
  if (!key(CHAR_D)) {
    float t = iTime;
    if (mod(index.x+index.y,4.0) < 2.0) t = -t;
    vec2 dotpos = ccentre + s*vec2(sin(t),cos(t));
    float dotdist = distance(p,dotpos);
    col = mix(vec3(1),col,smoothstep(0.015,0.025+pwidth,dotdist));
  }
  return col;
}

bool checksegment(vec2 index, vec2 s, float r, vec2 offset) {
  if (key(CHAR_C)) return true;
  vec2 p = 0.5*vec2(1,X)*abs(index+s*offset);
  return dot(p,p) < r*r;
}

vec3 diagram(vec2 p) {
  float scale = 3.0;
  p *= scale;
  vec2 p0 = p;
  pwidth = fwidth(p0.x);
  p.y /= X;
  vec2 index = round(p); // Coords of centre
  p -= index;
  index *= 2.0;
  p.y *= X;
  vec3 col = mod(index.x+index.y,4.0)< 2.0 ? vec3(1) : vec3(0.8);
  vec2 s = ssign(p);
  p = abs(p);
  vec3 ccol = vec3(0);
  float r = mod(iTime,6.0);
  if (checksegment(index,s,r,vec2(1,1))) {
    if (checksegment(index,s,r,vec2(-1,1))) {
      col = docircle(col,p,index,s,vec2(0,2));
    }
    if (checksegment(index,s,r,vec2(2,0))) {
      col = docircle(col,p,index,s,vec2(3,1));
    }
  }
  if (checksegment(index,s,r,vec2(0,0))) {
    if (checksegment(index,s,r,vec2(1,1))) {
      col = docircle(col,p,index,s,vec2(2,0));
      col = docircle(col,p,index,s,vec2(-1,1));
    }
    if (checksegment(index,s,r,vec2(-1,1))) {
      col = docircle(col,p,index,s,vec2(-2,0));
    }
    if (checksegment(index,s,r,vec2(2,0))) {
      col = docircle(col,p,index,s,vec2(1,-1));
    }
  }
  if (!key(CHAR_C)) col = mix(vec3(0),col,smoothstep(0.0,pwidth,abs(length(p0)-r)));
  return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  vec3 col = diagram(p);
  col = pow(col,vec3(0.4545));
  fragColor = vec4(col,1);
}

