// License CC0 - "Circular" Pattern Tiling
//  I wanted to create "circular" tiling, turned out ok
//  mod1  - From: http://mercury.sexy/hg_sdf/
//  star5 - From: https://iquilezles.org/articles/distfunctions2d

#define PI  3.141592654
#define TAU (2.0*PI)

float mod1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize, size) - halfsize;
  return c;
}

vec2 toRect(vec2 p) {
  return p.x*vec2(cos(p.y), sin(p.y));
}

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

// Like many tiling functions modifies the input argument
//  and returns a vector indicating which tile we are in
vec3 modCircularPattern(inout vec2 p) {
  vec2 pp = toPolar(p);

  float nx = floor(pp.x + 0.5);
  pp.x -= nx;
  mod1(pp.x, 1.0);
  pp.x += nx;
  float cy = floor(0.5*nx*TAU)*2.0;

  float ny = mod1(pp.y, TAU/cy);
  if(nx > 0.0) {
    p = toRect(pp) - vec2(nx, 0.0);
    return vec3(nx, mod(ny+cy*0.5, cy), cy);
  } else {
    return vec3(0.0, 0.0, 1.0);
  }
}

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x+s*p.y, -s*p.x+c*p.y);
}

float star5(vec2 p, float r, float rf) {
  const vec2 k1 = vec2(0.809016994375, -0.587785252292);
  const vec2 k2 = vec2(-k1.x,k1.y);
  p.x = abs(p.x);
  p -= 2.0*max(dot(k1,p),0.0)*k1;
  p -= 2.0*max(dot(k2,p),0.0)*k2;
  p.x = abs(p.x);
  p.y -= r;
  vec2 ba = rf*vec2(-k1.y,k1.x) - vec2(0,1);
  float h = clamp( dot(p,ba)/dot(ba,ba), 0.0, r );
  return length(p-ba*h) * sign(p.y*ba.x-p.x*ba.y);
}

float df(vec2 p) {
  const float lw = 0.033;

  float nx = floor(length(p) + 0.5);
  rot(p, iTime/sqrt(1.0+nx));

  vec3 n = modCircularPattern(p);

  rot(p, 0.1*TAU*(n.y+n.x));;

  float d = star5(p, 0.5-lw, 0.5);  
  d = abs(d) - lw;

  return d;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  vec2 q = fragCoord.xy / iResolution.xy;
  vec2 p = -1.0 + 2.0*q;
  p.x *= iResolution.x/iResolution.y;

  float aa = 2.0/iResolution.y;

  float s = 0.25*mix(0.1, 1.0, 0.5+0.5*sin(iTime*0.5));
  float d = df(p/s)*s;

  vec3 col = mix(vec3(1.0), vec3(0.125), tanh(length(0.05*p/s)));

  col = mix(col, vec3(0.0), smoothstep(-aa, aa, -d));
  col = pow(col, vec3(1.0/2.2));

  fragColor = vec4(col,1.0);
}

//function to slide shapes
vec4 moveShapes(vec2 st, float scale, float speed){
    st *= scale;
    float time = iTime*speed;
    if( fract(time)>0.5 ){
        if (step(1.,mod(st.y,2.))>0.){
            st.x += 2.*fract(time);
        } else {
            st.x -= 2.*fract(time);
        }
    } else {
        if (step(1.,mod(st.x,2.))>0.){
            st.y += 2.*fract(time);
        } else {
            st.y -= 2.*fract(time);
        }
    }
    return vec4(fract(st),floor(st));
}

//the Rose curve
float rose(vec2 st){
    float gray;
    vec2 pos = st-vec2(0.5);
    float r =(2.+.5*sin(iTime))*length(pos);
    float a = atan(pos.y,pos.x);
    float f =abs(cos(a*(1.+mod(5.+5.*sin(iTime),11.))))*.7+.1;
	gray=smoothstep(f-.5,f,r)-smoothstep(f,f+0.5,r);
    return gray;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);
	//set the number of shapes by the value of scale
    float scale=6.+10.*iMouse.x/iResolution.x;
    //set the speed of sliding
    float speed=.5;
    vec4 uvij = moveShapes(uv,scale,speed);
    float shape=rose(uvij.xy);
    //flag is used to alternate colors
   float flag=mod(uvij.z+uvij.w,2.);
    color = shape*vec3(1.,flag,flag);

    fragColor = vec4(color,1.0);
}

// License: CC0 (https://creativecommons.org/publicdomain/zero/1.0/)

/*
    Approximations for the Bessel functions J0 and J1 and the Struve functions H0 and H1.
    https://en.wikipedia.org/wiki/Bessel_function
    https://en.wikipedia.org/wiki/Struve_function
*/

// https://link.springer.com/article/10.1007/s40314-020-01238-z
float BesselJ0(float x)
{
    float xx = x * x;
    float lamb = 0.865;
    float q    = 0.7172491568;
    float p0   = 0.6312725339;
    float ps0  = 0.4308049446;
    float p1   = 0.3500347951;
    float ps1  = 0.4678202347;
    float p2   =-0.06207747907;
    float ps2  = 0.04253832927;

    float lamb4 = (lamb * lamb) * (lamb * lamb);
    float t0 = sqrt(1.0 + lamb4 * xx);
    float t1 = sqrt(t0);

    return xx == 0.0 ? 1.0 : 1.0/(t1 * (1.0 + q * xx)) * ((p0 + p1*xx + p2*t0) * cos(x) + ((ps0 + ps1*xx) * t0 + ps2*xx) * (sin(x)/x));
}

// https://www.sciencedirect.com/science/article/pii/S2211379718300111
float BesselJ1(float x)
{
    float xx = x * x;

    return (sqrt(1.0 + 0.12138 * xx) * (46.68634 + 5.82514 * xx) * sin(x) - x * (17.83632 + 2.02948 * xx) * cos(x)) /
           ((57.70003 + 17.49211 * xx) * pow(1.0 + 0.12138 * xx, 3.0/4.0) );
}

// https://research.tue.nl/nl/publications/efficient-approximation-of-the-struve-functions-hn-occurring-in-the-calculation-of-sound-radiation-quantaties(c68b8858-9c9d-4ff2-bf39-e888bb638527).html
float StruveH0(float x)
{
    float xx = x * x;

    return BesselJ1(x) + 1.134817700  * (1.0 - cos(x))/x - 
                         1.0943193181 * (sin(x) - x * cos(x))/xx - 
                         0.5752390840 * (x * 0.8830472903 - sin(x * 0.8830472903))/xx;
}

// https://research.tue.nl/nl/publications/efficient-approximation-of-the-struve-functions-hn-occurring-in-the-calculation-of-sound-radiation-quantaties(c68b8858-9c9d-4ff2-bf39-e888bb638527).html
float StruveH1(float x)
{
    const float pi = 3.14159265359;

    float xx = x * x;

    return 2.0/pi - BesselJ0(x) + 0.0404983827 * sin(x)/x + 
                                  1.0943193181 * (1.0 - cos(x))/xx - 
                                  0.5752390840 * (1.0 - cos(x * 0.8830472903))/xx;
}


void mainImage( out vec4 outCol, in vec2 uv0 )
{
	vec2 uv = uv0 - 0.5;
    uv0 += 0.0;

    vec2 tex = uv0;
    tex -= iResolution.y * 0.5;
    //tex /= iResolution.xx * 0.125;
    tex *= vec2(64,8) / iResolution.x;

    vec3 col = vec3(0.0);

	col = mix(vec3(1.0), vec3(0.9), Grid(tex.xy * 4.0, 0.0));        
	col = mix(col, vec3(0.5), Grid(tex.xy * 1.0, 0.0));        
	col = mix(col, vec3(0.125), Cross(tex.xy * 1.0, 0.)); 	


    col = mix(col, vec3(1.0, 0.5, 0.0), Graph(StruveH1(tex.x) - tex.y, 0.5) * 0.75);
    col = mix(col, vec3(0.0, 1.0, 0.5), Graph(StruveH0(tex.x) - tex.y, 0.5) * 0.75);

    col = mix(col, vec3(0.0, 0.5, 1.0), Graph(BesselJ1(tex.x) - tex.y, 0.5));       
    col = mix(col, vec3(1.0, 0.0, 0.0), Graph(BesselJ0(tex.x) - tex.y, 0.5)); 
    //col = mix(col, vec3(0.0, 1.0, 0.0), Graph(Foo(tex.x) - tex.y, 0.5)); 

    #if 1
    vec2 s = (uv0/iResolution.xy*2.0-1.0);
    s.x = 1.0-Pow2(s.x);    s.y = 1.0-Pow2(s.y);
    col *= mix(1.0, 0.4, Pow2(1.0-sqrt(s.x*s.y)));
    #endif

    outCol = vec4(GammaEncode(clamp01(col)), 1.0);    

}

