float mapScene(in vec3 p) {
    vec3 a = vec3(cos(iTime), 2.0 * sin(iTime), cos(iTime * 2.0)); // Opening of the tube
    vec3 b = vec3(0.0, 0.0, 0.0); // Base of the tube

    vec3 ba = a - b;
    vec2 sp = vec2(atan(ba.z, ba.x), atan(ba.y, length(ba.xz)));
    vec4 cs = vec4(cos(sp), sin(sp));

    p -= b; // Translate to the base
    p.xz *= mat2(cs.x, cs.z, -cs.z, cs.x); // Rotate to point along axis ba
    p.xy *= mat2(cs.y, cs.w, -cs.w, cs.y);

    vec2 size = vec2(cos(iTime) + 1.0, sin(iTime * 2.0) + 2.0); // radius, height
    vec2 pr = vec2(length(p.yz), p.x); // Revolve the shape

    // Can you spot those sneaky branches?
    float tube = pr.y < 0.0 ? abs(length(pr) - size.x) : pr.y < size.y ? abs(abs(pr.x) - size.x) : length(abs(pr - vec2(0.0, size.y)) - vec2(size.x, 0.0));

    return tube - 0.25 + 0.249 * sin(iTime);
}

vec3 getNormal(in vec3 p) {
    return normalize(vec3(mapScene(p + vec3(0.001, 0.0, 0.0)) - mapScene(p - vec3(0.001, 0.0, 0.0)),
                          mapScene(p + vec3(0.0, 0.001, 0.0)) - mapScene(p - vec3(0.0, 0.001, 0.0)),
                          mapScene(p + vec3(0.0, 0.0, 0.001)) - mapScene(p - vec3(0.0, 0.0, 0.001))));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, 0.0, 5.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    float dt = 0.0;
    for (int iter=0; iter < 150; iter++) {
        vec3 p = ro + rd * dt;
        float d = mapScene(p);
        if (d < 0.001) {
            vec3 n = getNormal(p);
            vec3 l = vec3(-0.58, 0.58, 0.58);
            fragColor.rgb += max(0.2, dot(n, l));

            break;
        }

        if (dt > 10.0) {
            break;
        }

        dt += d;
    }
}

const int drops = 500;
const float dropSizeMin = 0.0025;
const float dropSizeMax = 0.004;
const float dropDistortionScale = 0.3;
const float dropDistortion = 0.003;
const float dropSpeedMin = 0.0;
const float dropSpeedMax = 0.6;
const float dropJiggleAmount = 0.015;
const float dropJiggleScale = 0.4;

const float bgBlurRadius = 5.;

const float pi = 3.14159265;

//Taken from: https://gist.github.com/keijiro/24f9d505fac238c9a2982c0d6911d8e3
uint hash(uint s)
{
    s ^= 2747636419u;
    s *= 2654435769u;
    s ^= s >> 16;
    s *= 2654435769u;
    s ^= s >> 16;
    s *= 2654435769u;
    return s;
}

float random(uint seed)
{
    return float(hash(seed)) / 4294967295.0; // 2^32-1
}

float sat(float x)
{
    return max(min(x,1.),0.);
}

float remap(float x, float mi, float ma)
{
    return (x-mi)/(ma-mi);
}

float remapTo(float x, float mi, float ma)
{
    return x*(ma-mi)+mi;
}

vec3 blur(sampler2D tex, vec2 uv)
{
    vec3 col = vec3(0.);
    float sum = 0.;

    for(float x = -bgBlurRadius; x < bgBlurRadius; x++)
    {
        for(float y = -bgBlurRadius; y < bgBlurRadius; y++)
        {
            /*if(sqrt(x*x+y*y)>r)
                continue;*/
            vec2 off = vec2(x,y)/bgBlurRadius;
            vec2 offFrost = tan(off*pi/2.)*(2./pi);

            float l = 1.-sqrt(x*x+y*y)/bgBlurRadius;
            off = off;
            float weight = tan(l);

            off *= 20.;
            sum += weight;
            col += textureLod(tex, uv + off/iResolution.xy,3.).rgb*weight;
        }
    }
    return col / sum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvn = fragCoord/iResolution.xx;

    vec2 dist = (texture(iChannel1, uv/dropDistortionScale).rg*2.-1.) * dropDistortion;
    uv += dist;
    uvn += dist;

    vec3 uvOff = vec3(0.);
    for(int d = 0; d < drops; d++)
    {
        //Position and size
        vec2 dp = vec2(random(uint(d)), random(uint(d+drops)));
        float dropSize = mix(dropSizeMin, dropSizeMax, random(uint(d+drops*2)));

        //Animate
        float t = iTime+sat(pow(sin(iTime * sat(random(uint(d+drops*8))*.5+.5)), 2.));
        float vel = pow(remapTo(random(uint(d+drops*5)), dropSpeedMin, dropSpeedMax), 2.2);
        dp.y -= t * vel;
        dp.y = fract(dp.y);
        dp.x += (dropJiggleAmount*(.4-vel))*(texture(iChannel1, dp/dropJiggleScale).r*2.-1.);
        float stretch = pow(vel,1.5)*8.;

        float dropShape = 1.-sat(remap(sat(1.-length((dp-uvn)/vec2(1.-stretch*.2,1.+stretch))), 1.-dropSize, 1.));
        dropShape = sqrt(1.-dropShape*dropShape);

        vec2 vecAng = (normalize(uvn-dp));
        dropShape = (1.-dropShape)*smoothstep(0.1, 0.8,dropShape);
        uvOff.r += dropShape*vecAng.x;
        uvOff.g += dropShape*vecAng.y;
        uvOff.b += dropShape;
    }

    // BG Tex
    vec3 col = textureLod(iChannel0, uv+uvOff.rg*0.1, 3.).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
    //fragColor = vec4(vec3(smoothstep(0.5,0.95,sat(1.-uvOff.b+uvOff.g*.5))*0.25+.75),1.0);
    float mixVal = smoothstep(0.5,0.95,sat(1.-uvOff.b+uvOff.g*.5))*0.5+.5;
    //fragColor = vec4(mix(vec3(1.), col, 1.-mixVal),1.0);
    //fragColor = vec4(mix(blur(iChannel0, fragCoord/iResolution.xy), col, 1.-mixVal),1.0);
}

