float mapClouds(vec3 p){
    float rotEnv = 0.125*iTime;
    float currHeight = length(p);

    p.yz *= rot(rotEnv);


    p *= 0.5;
    float cloudsRange = (cloudsHigherLimit - cloudsLowerLimit)*0.494;
    float cloudsMid = cloudsLowerLimit + cloudsRange;

    float f = valueNoiseCheap(p*0.25,1.);

    f += valueNoiseCheap(p*0.51,1.)*0.325;
    f += valueNoiseCheap(p*1.005,1.)*0.254;
    f += valueNoiseCheap(p*2.04,1.)*0.105;

    f *= pow(valueNoiseCheap(p*0.2 + iTime*0.2,1.)*1.,2.9);

    f = smoothstep(0.,1.,f*1.2);

    f *= pow( smoothstep( 1.,0., abs(currHeight - cloudsHigherLimit + cloudsRange)/cloudsRange*0.94), 0.99);

    return f * 1.;
}


float sdRocks(vec3 _p, float md, float s){
  vec4 p = vec4(_p,1.);

  p.yw *= rot(0.4);
  p = abs(p);
  p.xz *= rot(0.4);
  p = abs(p);

  p.xy *= rot(2.4);
  p = abs(p) - 1.4;

  p.yz *= rot(.2);
  p = abs(p);
  p = pmod(p,md);

  p = abs(p) - s;
  return max(p.x,max(p.y,max(p.z,p.w))) ;
}

