        if(d.x < 0.02){
            hit = true;
            break;
        } else if (t > 29.){
            break;
        }

        p = ro + rd*(t += d.x);
    }
    float seaDepthFactor = 0.;
    vec3 seaHitP = p;
    float tSea = 0.;
    if (hit && d.y == 2.){
        // Sea marching
        marchInsideSea = true;
        seaDepthFactor = smoothstep(1.,2.6,fa*2.);
        albedo = mix(vec3(0.,0.5,1)*0.9,vec3(0.21,0.2,0.6),seaDepthFactor);
        for(int i = 0; i < 20; i++){
            vec2 d = map(p);

            if( d.x < 0.004){
                hit = true;
                marchInsideSea = false;

                break;
            }

            p = seaHitP + rd*(tSea += d.x);
        }


    }

    vec3 sunPos = normalize(vec3(.9,2.4,2));
    //sunPos.yz *= rot(sin(iTime*0.5)*1.5);
    float spmd = sin(iTime*0.5)*1.5;
    sunPos.yz += vec2(sin(spmd),cos(spmd));


    //sunPos.xz *= rot(sin(iTime));
    sunPos *= (planetSz + 50.);

    vec3 sunDir = normalize(sunPos - p);

    vec3 sunCol = vec3(0.9,0.9,0.9)*0. + vec3(1,0.9,0.7);

    float depthView; 
    vec3 atmosphere = getAtmosphere( ro, rd, depthView, t, hit, sunPos);


    // Shading

    if(hit){

        marchInsideSea = true;
        vec3 n = getNormal(p);
        marchInsideSea = false;

        vec3 hf = normalize(sunDir + n);
        float diff = max(dot(n,sunDir),0.);
        float spec = pow(max(dot(n,hf),0.),32.);

        vec3 groundAlbedo = vec3(0.6,1.2,0.4)*1.;
        groundAlbedo = vec3(0.4,1.2,0.4)*1.;

        vec3 sandAlbedo = vec3(0.86,0.8,0.5)*1.6;
        groundAlbedo = mix(sandAlbedo, groundAlbedo,smoothstep(0.,0.6,length(p) - planetSz - 2.9));
        #define SSS(a) clamp(map(p + sunDir).x/a,0., 1.)
        float sssfact = SSS(0.5)*SSS(4.5)*4.5;
        //sssfact = 1.;
        vec3 groundColor = groundAlbedo*(diff + 0.03 + sssfact*(1. + diff)*0.1)*(sunCol*0.6 + atmosphere) + spec*0.05*(sunCol + atmosphere);

        if (d.y == 3.){
            groundColor = 0.2*sandAlbedo*(diff + 0.03)*(sunCol + atmosphere) + spec*0.4*sunCol;
            col += groundColor;
        }

        if(d.y == 1.){
            col += groundColor;
        } else if (d.y == 2.){

            vec3 seaN = getNormal(seaHitP);

            vec3 seaSunDir = normalize(sunPos - seaHitP);

            vec3 seaHf = normalize(seaSunDir + seaN);
            float seaDiff = max(dot(seaN,seaSunDir),0.);
            float seaSpec = pow(max(dot(seaN,seaHf),0.),22.);
            float seaFres = pow(1. - max(dot(seaN,-rd),0.),5.);

            vec3 vP = seaHitP;
            vP.yz *= rot(0.125*iTime);
            float foam = voronoi(normalize(vP)*(136. + sin(iTime*0.05)*40.)*0.4 + vP).x;

            foam *= pow(valueNoiseCheap(normalize(vP)*(136. + sin(iTime*0.3)*40.)*0.1,1.), 1.2 ) ;
            //v *= 1. - v;
            col += (albedo + smoothstep(0.1,0.,dland - 0. - foam))*(seaDiff*0.5 + 0.1)*(sunCol + atmosphere)*0.8 
            + (seaSpec + seaFres)*0.2*(sunCol*0.3 + 0.2*atmosphere + albedo*0.4);
            col *= 1. + seaDepthFactor*groundColor*26.;
        }
    }

    // Atmosphere

    atmosphere *= 1.-exp(-t*0.2);


    if(hit){
       atmosphere *= 1.-pow(exp(-(t + tSea*2.)*0.05 ),2.);
    }

    col = col * exp(-depthView) + atmosphere; 

    //col += accumAtmo*1.;

    // Clouds

    float cloudSteps = 26.;

    float currH = length(ro);



    vec2 lowerSphere = sphIntersect( ro, rd, vec3(0), cloudsLowerLimit );
    vec3 lowerLimitP = ro + rd * lowerSphere.y;


    //if(currH > cloudsLowerLimit && currH < cloudsHigherLimit) {
    if(currH > cloudsLowerLimit  ) {

        //lowerLimitP = ro;
    }

    vec3 higherLimitP = lowerLimitP + rd * sphIntersect( lowerLimitP, rd, vec3(0), cloudsHigherLimit ).y;

    vec3 cloudP = lowerLimitP;

    float cloudLength = length(higherLimitP - lowerLimitP); // TODO: can make this cheaper
    float cloudStepSz = cloudLength/(cloudSteps);

    float cloudDensTotal = 0.;
    vec3 cloudAccum = vec3(0.);


    for(float i = 0.; i < cloudSteps; i++){
        float d = mapClouds(cloudP);

        sunDir = normalize(sunPos - cloudP);
        float difffact = clamp( d*1. - mapClouds(cloudP + sunDir*1.4)*1.1 , 0., 1. );
        vec3 diff = mix( atmosphere*0.1 + vec3(0.04,0.11,0.2)*(0.4 - smoothstep(0.,1.,1. - atmosphere*1.8)*0.4), (vec3(1,0.9,0.9)*1. + sunCol*0.4 + atmosphere*.4)*0.5, difffact );
        vec3 absorption = mix( vec3(1,0.9,0.8), vec3(1,0.9,0.7)*0.5, clamp( cloudDensTotal*0.5, 0., 1. ) );
        vec3 fringe = vec3(0.9,0.8,0.5)*clamp( 1. - d*4., 0.,1.);

        d = d*(1.-cloudDensTotal)*cloudStepSz;

        cloudDensTotal += d;
        cloudAccum += d*(diff*3.7*absorption + fringe*(0.1 + diff*0.9)*2.64);

        // map(cloudP).x < 0.


        if( cloudDensTotal > 1.){
            break;
        }
        cloudP += rd*cloudStepSz;
    }
    if (!hit){
        col = mix(col,cloudAccum , pow(clamp(cloudDensTotal*1. - 0.,0.,1.),5.));
    }

    // sun 

    //sunPos.yz *= rot(0.25);

    sunPos.yz *= rot(0.25*(1.-sphericalEnv));
    vec2 sunUV = mix( uv, uv+2000., sphericalEnv);

    vec3 sun = vec3(0);
    vec3 sunDirNormalized = normalize(sunPos - ro);
    //sunDirNormalized = mix( sunDirNormalized, getRdSpherical(ro, sunUV), sphericalEnv);
    sunDirNormalized = mix( sunDirNormalized, getRdSpherical(ro, sunDirNormalized.xy), sphericalEnv);


    if (!hit){
        sun += .5* smoothstep(1.,0.,length(sunUV - sunDirNormalized.xy) + .44)*vec3(1,0.6,0.4);
        sun += .5* smoothstep(0.5,0.,length(sunUV - sunDirNormalized.xy) + .24)*vec3(2,0.9,0.4);
        sun += smoothstep(0.08,0.,length(sunUV - sunDirNormalized.xy) - .03)*vec3(1,0.8,0.9);
    }
    float sunCloudBlend = smoothstep( 0. ,1. , 1. - cloudDensTotal*0.8 );
    col += sun*sunCloudBlend;

    //sphericalEnv
    float sunRaysBlend = smoothstep(0.,1.,  
        mix(
            (sunDirNormalized.y +  0.24 - smoothstep(0.,0.4,abs(sunDirNormalized.y) - 0.4 ) )*4.,
            ( 2.4 - length(sunUV - sunDirNormalized.xy) )*19.*0. + 0.,
            sphericalEnv
            ))
            ;
    col += sunRaysBlend*sunCloudBlend*getSun(uv, sunUV, sunDirNormalized.xy, sunCol*0.2 + vec3(1,0.6,0.4)*0.8);
    col = mix(col,smoothstep(0.,1.,col*1.6),0.5);
    col = max(col,0.);
    col = mix(acesFilm(col), col, 0.9);
    col *= 1. - dot(uv,uv*0.4)*2.1;

    col = pow(col,vec3(0.454545));
    fragColor = vec4(col,1.0);
}

