/*
    Maelstrom by nachocpol@gmail.com

    From the SW Solo movie
    Refs:
        https://www.retrozap.com/wp-content/uploads/2018/04/Screen_Shot_2018_02_05_at_8_51_04_AM.png
        https://www.the-blueprints.com/blueprints-depot-restricted/sciencefiction/star-wars-imperial/imperial_sd-02668.jpg
*/

#define PI 3.14159265359
#define DEG_TO_RAD 0.0174533
#define THUNDER 1 // Disable this if you are sensitive to flashing lights

const float k_Near = 1.0;
const float k_Far = 100.0;
const int k_MainSteps = 360;
const float k_HitDelta = 0.001;

const float k_ThunderRadius = 20.0;
const vec3 k_ThunderColor = vec3(0.5, 0.61, 0.75); 
const vec3 k_Thunder0 = vec3(-15.0, -10.0, -5.0);
const vec3 k_Thunder1 = vec3(10.0, 9.0, -5.0);
const vec3 k_Thunder2 = vec3(-10.0, 9.0, 5.0);
const float k_SunRadius = 30.0;
const vec3 k_SunColor = vec3(1.0, 0.35, 0.0);
const vec3 k_Sun = vec3(-5.0, 10.0, 28.0);

const vec3 k_CloudAlbedo = vec3(0.2, 0.18, 0.28);

float saturate(float v)
{
    return max(min(v,1.0),0.0);
}

mat3 RotX(float angle)
{
    float o = angle * DEG_TO_RAD;
    float cos0 = cos(o);
    float sin0 = sin(o);
    return mat3(1.0, 0.0, 0.0, 0.0, cos0, -sin0, 0.0, sin0, cos0);
}

mat3 RotY(float angle)
{
    float o = angle * DEG_TO_RAD;
    float cos0 = cos(o);
    float sin0 = sin(o);
    return mat3(cos0, 0.0, sin0, 0.0, 1.0, 0.0, -sin0, 0.0, cos0);
}

mat3 RotZ(float angle)
{
    float o = angle * DEG_TO_RAD;
    float cos0 = cos(o);
    float sin0 = sin(o);
    return mat3(cos0, -sin0, 0.0, sin0, cos0, 0.0, 0.0, 0.0, 1.0);
}

// Sphere (r radius)
float SphereD(vec3 p, float r)
{
    return length(p) - r;
}

float InfCylinderD(vec3 p, vec3 c)
{
    // Flipped to get the inside of the cylinder
    return c.z - length(c.xy - p.xy );
}

// Box
float BoxD(vec3 p, vec3 b)
{
    vec3 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

// Triangular prism (h.x hight, h.y length)
float TriPrismD(vec3 p, vec2 h)
{
  vec3 q = abs(p);
  return max(
      q.z - h.y,
      max(q.x * 0.866025 + p.y * 0.5, -p.y) - h.x * 0.5
  );
}

// https://www.shadertoy.com/view/4dS3Wd
float hash(float p) 
{ 
    p = fract(p * 0.011); 
    p *= p + 7.5; 
    p *= p + p; 
    return fract(p); 
}

float ValueNoise1D(float x) 
{
    float i = floor(x);
    float f = fract(x);
    float u = f * f * (3.0 - 2.0 * f);
    return mix(hash(i), hash(i + 1.0), u);
}

// https://www.shadertoy.com/view/4sfGzS
float ValueNoise3D(vec3 x)
{
    vec3 i = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
	vec2 uv = (i.xy+vec2(37.0,17.0)*i.z) + f.xy;
	vec2 rg = textureLod(iChannel1, (uv+0.5)/256.0, 0.0).yx;
	return mix( rg.x, rg.y, f.z );
}

float Fbm(vec3 p)
{
    float n = ValueNoise3D(p * 1.0) * 1.0;
    n += ValueNoise3D(p * 2.4) * 0.55; 
    n += ValueNoise3D(p * 5.84) * 0.3025;
    return n / 1.8525;
}

float ScaleNorm(vec3 s)
{
    return min(min(s.x, s.y), s.z);
}

float StarDestroyerDetail(vec3 p)
{
    float n = texture(iChannel0, p.xz * 0.01).r;
    n *= 0.135;
    return n;
}

float StarDestroyer(vec3 p)
{
    float hullLen = 20.0;
    float halfHullLen = hullLen * 0.5;
    float hull = 0.0;
    {
        vec3 pHull = RotZ(90.0) *  p;
        float lenFactor = saturate((p.z + halfHullLen) / hullLen) + 0.025;
        vec3 hullScale = vec3(lenFactor * 0.5, lenFactor , 1.0);
        pHull.xyz /= hullScale;
        pHull = abs(pHull);
        hull = TriPrismD(pHull, vec2(10.0, halfHullLen)) * ScaleNorm(hullScale);
    }

    float hullSmall = 0.0;
    {
        float halfHullSmallLen = 5.0;
        vec3 pHullSmall = RotZ(90.0) *  (p - vec3(0.0,1.0,2.0));
        float lenFactor = saturate((p.z + halfHullSmallLen) / hullLen) + 0.025;
        vec3 hullScale = vec3(lenFactor * 0.7, lenFactor , 1.0);
        pHullSmall.xyz /= hullScale;
        pHullSmall = abs(pHullSmall);
        hullSmall = TriPrismD(pHullSmall, vec2(10.0, halfHullSmallLen)) * ScaleNorm(hullScale);
    }

    float bridgeNeck = 0.0;
    {
        vec3 bridgeP = p - vec3(0.0, 3.0, halfHullLen - 0.8);
        bridgeNeck = BoxD(bridgeP, vec3(0.8, 2.5, 0.8));
    }

    float bridgeHead = 0.0;
    {
        vec3 bridgeP = p - vec3(0.0, 4.5, halfHullLen - 0.8);
        float yx = max(1.0 - abs(p.x / 3.0), 0.45);
        bridgeHead = BoxD(bridgeP, vec3(4.0, 0.9 * yx, 0.8));
    }

    float radarSphere = 0.0;
    {
        vec3 radarP = p;
        radarP.x = abs(radarP.x);
        radarP -=vec3(3.0, 5.3, halfHullLen - 0.8);
        radarSphere = SphereD(radarP, 0.8);
    }

    return min(min(hull,hullSmall), min(min(bridgeNeck, bridgeHead), radarSphere)) 
        + StarDestroyerDetail(p);
}

float SceneDistance(vec3 p)
{
    return StarDestroyer(p);
}

vec3 SceneNormal(vec3 p)
{
    vec2 delta = vec2(k_HitDelta, 0.0);
    return normalize(vec3(
        SceneDistance(p + delta.xyy) - SceneDistance(p - delta.xyy),
        SceneDistance(p + delta.yxy) - SceneDistance(p - delta.yxy),
        SceneDistance(p + delta.yyx) - SceneDistance(p - delta.yyx)
    ));
}

float March(vec3 ro, vec3 rd)
{
    float dist = k_Near;
    vec3 p = ro + rd * dist;
    for(int i = 0; i < k_MainSteps; ++i)
    {
        float curDist = SceneDistance(p);
        if(((curDist) < k_HitDelta) || (dist >= k_Far))
        {
            return dist;
        }
        dist += curDist;
        p = ro + rd * dist;
    }
    return k_Far;
}

vec4 GetThunderActivation()
{
    vec4 act;
    act.x = pow(abs(max(ValueNoise1D(iTime * 12.0), 0.0)-0.2), 5.0);
    act.y = pow(abs(max(ValueNoise1D((iTime  - 10.0)* 12.0), 0.0)-0.2), 10.0);
    act.z = pow(abs(max(ValueNoise1D((iTime  + 10.0) * 12.0), 0.0)-0.1), 10.0);
    act.w = saturate(pow(abs(sin(iTime * 7.0) * cos(iTime * 3.0)), 1.0) + 0.5);
    return act;
}

vec3 CumputePointLight(vec3 lightPosition, float lightRadius, vec3 lightColor, vec3 position, vec3 normal, vec3 baseColor)
{
    vec3 toLight = lightPosition - position;
    vec3 l = normalize(toLight);
    float dist = length(toLight);
    float atten = saturate(1.0 - (dist / lightRadius));
    atten *= atten;
    float NdotL = max(dot(normal, l), 0.0);
    return (baseColor / PI) * lightColor * atten * NdotL;
}

vec3 ShadeScene(vec3 ro, vec3 rd, float d)
{
    vec3 radiance = vec3(0.0);

    vec3 position = ro + rd * d;
    vec3 normal = SceneNormal(position);

    vec3 colA = vec3(0.8);
    vec3 colB = vec3(0.7);
    vec3 baseColor = mix(colA, colB, texture(iChannel0, position.xy * 0.2).r);

    vec4 thunderAct = GetThunderActivation();
    vec3 directLight = vec3(0.0);
#if THUNDER
    directLight += CumputePointLight(
        k_Thunder0, k_ThunderRadius, k_ThunderColor, position, normal, baseColor
    ) * thunderAct.x * 10.0;
    directLight += CumputePointLight(
        k_Thunder1, k_ThunderRadius, k_ThunderColor, position, normal, baseColor
    ) * thunderAct.y * 10.0;
    directLight += CumputePointLight(
        k_Thunder2, k_ThunderRadius, k_ThunderColor, position, normal, baseColor
    ) * thunderAct.z * 10.0;
#endif
    directLight += CumputePointLight(
        k_Sun, k_SunRadius, k_SunColor * 25.0, position, normal, baseColor
    ) * thunderAct.w;

    vec3 indirect = (k_CloudAlbedo * 0.05) * (baseColor / PI);
    vec3 litColor = directLight + indirect;

    vec3 fogColor = vec3(0.01);
    float fogIntensity = saturate(d / 50.0);
    fogIntensity *= fogIntensity;

    return mix(litColor, fogColor, fogIntensity);
}

float CloudMask(vec3 p)
{
    float pz = max(p.z + 20.0, 0.0);
    pz *= p.z;
    p.x += (pz) * 0.01;
    p.y -= (pz) * 0.007;
    float cylRadius = 8.0;
    float dist = InfCylinderD(p, vec3(0.0, 4.0, cylRadius));
    float mask = saturate((1.0 - (dist/ cylRadius)) - 0.35);
    return dist < 0.0 ? 1.0 : mask;
}

float GetCloudDensity(vec3 p)
{
    float mask = CloudMask(p);
    p.z -= iTime * 0.25;
    p.x += iTime * 0.05;
    float density = saturate(Fbm(p * 0.3) - 0.25);
    return density * mask;
}

vec4 ShadeClouds(vec3 ro, vec3 rd, float scene)
{
    vec3 cloudColor = vec3(0.0);
    float absorption = 2.5;
    float powderBeta = 1.035;
    float powderAmmount = 1.151;
    float transmittance = 1.0;
    float opacity = 0.0;
    float stepSize = 0.8;
    float dist = 0.0;

    for(int i = 0; i < 128; ++i)
    {
        vec3 curPos = ro + rd * dist;

        float density = GetCloudDensity(curPos);
        float transmitCoef = density * absorption;
        float powderCoef = density * powderBeta;
        float beer = exp(-transmitCoef * stepSize);
        transmittance *= beer;

        if(transmittance < 0.01 || dist >= scene) 
        {
            break;
        }

        float powder = 1.0 - saturate(exp(-powderCoef * stepSize * 2.0) * powderAmmount);
        cloudColor += transmittance * k_CloudAlbedo * powder;

        // Apply thunder:
        vec4 thunderAct = GetThunderActivation();
#if THUNDER
        cloudColor += CumputePointLight(
            k_Thunder0, k_ThunderRadius, k_ThunderColor, curPos, normalize(k_Thunder0 - curPos), k_CloudAlbedo
        ) * density * 300.0 * transmittance * thunderAct.x;
        cloudColor += CumputePointLight(
            k_Thunder1, k_ThunderRadius, k_ThunderColor, curPos, normalize(k_Thunder1 - curPos), k_CloudAlbedo
        ) * density * 500.0 * transmittance * thunderAct.y;
        cloudColor += CumputePointLight(
            k_Thunder2, k_ThunderRadius, k_ThunderColor, curPos, normalize(k_Thunder2 - curPos), k_CloudAlbedo
        ) * density * 100.0 * transmittance * thunderAct.z;
#endif
         cloudColor += CumputePointLight(
            k_Sun, k_SunRadius, k_SunColor, curPos, normalize(k_Sun - curPos), k_CloudAlbedo
        ) * density * 3.0 * transmittance * thunderAct.w;

        // Ambient'ish term
        cloudColor += k_CloudAlbedo * 0.001;

        opacity = opacity + (1.0 - beer) * (1.0 - opacity);
        dist += stepSize;
    }

    return vec4(cloudColor, opacity);
}

mat3 LookAt(vec3 origin, vec3 target, float roll) 
{
  vec3 rr = vec3(sin(roll), cos(roll), 0.0);
  vec3 ww = normalize(target - origin);
  vec3 uu = normalize(cross(ww, rr));
  vec3 vv = normalize(cross(uu, ww));

  return mat3(uu, vv, ww);
}

vec3 GetCamPos()
{
    float t = iTime * 0.3;
    vec3 ro;
    ro.x = sin(t) + 2.0;
    ro.y = cos(t) + 3.0;
    ro.z = -25.0;

    if(iMouse.z > 0.0)
    {
        ro.x = ((iMouse.x / iResolution.x) * 2.0 - 1.0) * 10.0;
        ro.y = ((iMouse.y / iResolution.y) * 2.0 - 1.0) * -10.0;
    }
    return ro;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 ndc = uv * 2.0 - 1.0;
    ndc.x *= aspect;

    // Setup camera:
    vec3 ro = GetCamPos();    
    mat3 view = LookAt(ro, vec3(0.0), 0.0);
    vec3 rd = normalize(view * vec3(ndc, 1.3));

    // Shade the scene:
    float sceneDist = March(ro, rd);
    vec3 sceneColor = vec3(0.0); 
    if(sceneDist < k_Far)
    {
        sceneColor = ShadeScene(ro, rd, sceneDist);
    }
    // Shade and composite clouds:
    vec4 clouds = ShadeClouds(ro, rd, sceneDist);
    sceneColor = (sceneColor * (1.0 - clouds.a)) + clouds.rgb * clouds.a;


    // Post:
    fragColor.rgb = sceneColor;
    float v = 1.0 - (length(uv * 2.0 - 1.0) / 1.4);
    fragColor.rgb *= sqrt(v);
