///--------------------------------------------
// MASKS
float fillMask(float distanceChange, float dist) 
{
    return smoothstep(distanceChange, -distanceChange, dist);
}

float blurMask(float distanceChange, float dist, float blurAmount) {
    float blurTotal = blurAmount*.01;
    return smoothstep(blurTotal+distanceChange, -distanceChange, dist);
}

float innerMask(float distanceChange, float dist, float width) {
    return smoothstep(distanceChange,-distanceChange,dist+width);
}

float outerMask(float distanceChange, float dist, float width) {
    return smoothstep(distanceChange,-distanceChange,dist-width);
}


vec3 DibujoConBorde(vec2 uv, float dist, vec3 colIn,  vec3 colOut)
{   
    float distanceChange = fwidth(dist) * 0.5;
    float flm=fillMask(distanceChange, dist);
    float uWotM8 = 0.009;
    uWotM8 = outerMask(distanceChange, dist, uWotM8);
    colOut = mix(colOut, vec3(0,0,0), uWotM8);
    return mix(colOut,colIn, flm );
}

vec3 DibujoSinBorde(vec2 uv, float dist, vec3 colIn,  vec3 colOut)
{   
    float distanceChange = fwidth(dist) * 0.5;
    float flm=fillMask(distanceChange, dist);
    float uWotM8 = 0.009;
    uWotM8 = outerMask(distanceChange, dist, uWotM8);
    //colOut = mix(colOut, vec3(0,0,0), uWotM8);
    colOut = mix(colOut, colIn, uWotM8);
    return mix(colOut,colIn, flm );
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec2 rotate2d(vec2 p, float ang)
{
    float c = cos(ang); float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}

mat2 rot2D(float a)
{
    float c=cos(a);  float s=sin(a);
    return mat2(c,-s,s,c);
}

vec3 DibujoConBordeVec2(vec2 uv, vec2 vdist,   vec3 colOut)
{   
    vec3 colIn=getColorf(vdist.y); //color
    float dist=vdist.x; //distancia
    float distanceChange = fwidth(dist) * 0.5;
    float flm=fillMask(distanceChange, dist);
    float uWotM8 = 0.009;
    uWotM8 = outerMask(distanceChange, dist, uWotM8);
    colOut = mix(colOut, vec3(0,0,0), uWotM8);
    return mix(colOut,colIn, flm );
}


float sdUnevenCapsule( vec2 p, float r1, float r2, float h )
{
    p.x = abs(p.x);
    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);
    float k = dot(p,vec2(-b,a));
    if( k < 0.0 ) return length(p) - r1;
    if( k > a*h ) return length(p-vec2(0.0,h)) - r2;
    return dot(p, vec2(a,b) ) - r1;
}

