/*
Image encoding with neural network.

Neural network is trained to return pixel value for given uv coordinates.
Neural networks work poorly with low dimentional inputs and bias towards lower frequencies. 
Frequency transform is a way to encode uv location. It is taken from here: https://people.eecs.berkeley.edu/~bmild/fourfeat/index.html

Encoding embeds 2D input into 64D space. The neural network itself is just 2 layer fully-connected network:

layer 1: input 64, output 64. Dense layer. Activation Relu
layer 2: input 64, output 3, Dense layer. No activation

Values in b are sampled from gaussian distribution, so they could be recreated using the seed and RNG.
*/


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y = (1.0 - uv.y) * 0.5;
    uv -= vec2(0.5, 0.25);
    float a = iResolution.x / iResolution.y;
    uv = mix(vec2(uv.x / (1.7777 / a), uv.y), vec2(uv.x, uv.y * (1.7777 / a)), vec2(a < 1.7777));
    uv += vec2(0.5, 0.25);

    vec4 pos[16];
    for (int i = 0; i < 8; ++i)
    {
        vec4 a = 2.0 * 3.1415 * vec4(dot(uv, b[i * 4 + 0]), dot(uv, b[i * 4 + 1]), dot(uv, b[i * 4 + 2]), dot(uv, b[i * 4 + 3]));
        pos[i] = sin(a);
        pos[8 + i] = cos(a);
    }
    vec4 h[16];
    for (int i = 0; i < 16; ++i)
    {
        h[i] = cb[i];
    }
    for (int i = 0; i < 16; ++i)
    {
        h[0] += vec4(dot(cw0[i], pos[i]), dot(cw1[i], pos[i]), dot(cw2[i], pos[i]), dot(cw3[i], pos[i]));
        h[1] += vec4(dot(cw4[i], pos[i]), dot(cw5[i], pos[i]), dot(cw6[i], pos[i]), dot(cw7[i], pos[i]));
        h[2] += vec4(dot(cw8[i], pos[i]), dot(cw9[i], pos[i]), dot(cw10[i], pos[i]), dot(cw11[i], pos[i]));
        h[3] += vec4(dot(cw12[i], pos[i]), dot(cw13[i], pos[i]), dot(cw14[i], pos[i]), dot(cw15[i], pos[i]));
        h[4] += vec4(dot(cw16[i], pos[i]), dot(cw17[i], pos[i]), dot(cw18[i], pos[i]), dot(cw19[i], pos[i]));
        h[5] += vec4(dot(cw20[i], pos[i]), dot(cw21[i], pos[i]), dot(cw22[i], pos[i]), dot(cw23[i], pos[i]));
        h[6] += vec4(dot(cw24[i], pos[i]), dot(cw25[i], pos[i]), dot(cw26[i], pos[i]), dot(cw27[i], pos[i]));
        h[7] += vec4(dot(cw28[i], pos[i]), dot(cw29[i], pos[i]), dot(cw30[i], pos[i]), dot(cw31[i], pos[i]));
        h[8] += vec4(dot(cw32[i], pos[i]), dot(cw33[i], pos[i]), dot(cw34[i], pos[i]), dot(cw35[i], pos[i]));
        h[9] += vec4(dot(cw36[i], pos[i]), dot(cw37[i], pos[i]), dot(cw38[i], pos[i]), dot(cw39[i], pos[i]));
        h[10] += vec4(dot(cw40[i], pos[i]), dot(cw41[i], pos[i]), dot(cw42[i], pos[i]), dot(cw43[i], pos[i]));
        h[11] += vec4(dot(cw44[i], pos[i]), dot(cw45[i], pos[i]), dot(cw46[i], pos[i]), dot(cw47[i], pos[i]));
        h[12] += vec4(dot(cw48[i], pos[i]), dot(cw49[i], pos[i]), dot(cw50[i], pos[i]), dot(cw51[i], pos[i]));
        h[13] += vec4(dot(cw52[i], pos[i]), dot(cw53[i], pos[i]), dot(cw54[i], pos[i]), dot(cw55[i], pos[i]));
        h[14] += vec4(dot(cw56[i], pos[i]), dot(cw57[i], pos[i]), dot(cw58[i], pos[i]), dot(cw59[i], pos[i]));
        h[15] += vec4(dot(cw60[i], pos[i]), dot(cw61[i], pos[i]), dot(cw62[i], pos[i]), dot(cw63[i], pos[i]));
    }
    vec3 col = vec3(-0.0908, -0.2686, -0.5834);
    for (int i = 0; i < 16; ++i)
    {
        h[i] = max(h[i], vec4(0.));
        col += vec3(dot(pr[i], h[i]), dot(pg[i], h[i]), dot(pb[i], h[i]));
    }
    fragColor = vec4(col * 0.5 + 0.5, 1.0);
}

const float dots = 40.; //number of lights
const float radius = .25; //radius of light ring
const float brightness = 0.02;

//convert HSV to RGB
vec3 hsv2rgb(vec3 c){
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 p=(fragCoord.xy-.5*iResolution.xy)/min(iResolution.x,iResolution.y);
    vec3 c=vec3(0,0,0.1); //background color

    for(float i=0.;i<dots; i++){

		//read frequency for this dot from audio input channel 
		//based on its index in the circle
		float vol =  texture(iChannel0, vec2(i/dots, 0.0)).x;
		float b = vol * brightness;

		//get location of dot
        float x = radius*cos(2.*3.14*float(i)/dots);
        float y = radius*sin(2.*3.14*float(i)/dots);
        vec2 o = vec2(x,y);

		//get color of dot based on its index in the 
		//circle + time to rotate colors
		vec3 dotCol = hsv2rgb(vec3((i + iTime*10.)/dots,1.,1.0));

        //get brightness of this pixel based on distance to dot
		c += b/(length(p-o))*dotCol;
    }

    //black circle overlay	   
	float dist = distance(p , vec2(0));  
	c = c * smoothstep(0.26, 0.28, dist);

	fragColor = vec4(c,1);
}

#define M_PI 3.14159265359

vec4 rectangle(vec4 color, vec4 background, vec4 region, vec2 uv);
vec4 capsule(vec4 color, vec4 background, vec4 region, vec2 uv);
vec2 rotate(vec2 point, vec2 center, float angle);
vec4 bar(vec4 color, vec4 background, vec2 position, vec2 diemensions, vec2 uv);
vec4 rays(vec4 color, vec4 background, vec2 position, float radius, float rays, float ray_length, sampler2D sound, vec2 uv);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Prepare UV and background
    float aspect = iResolution.x / iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= aspect;
    vec4 color = mix(vec4(0.0, 0.0, 0.0, 1.0), vec4(0.0, 0, 0.0, 1.0), distance(vec2(aspect/2.0, 0.5), uv));

    //VISUALIZER PARAMETERS
    const float RAYS = 96.0; //number of rays //Please, decrease this value if shader is working too slow
    float RADIUS = 0.4; //max circle radius
    float RAY_LENGTH = 0.3; //ray's max length //increased by 0.1

    color = rays(vec4(1.0), color, vec2(aspect/2.0, 1.0/2.0), RADIUS, RAYS, RAY_LENGTH, iChannel0, uv);

    fragColor = color;
}

vec4 rays(vec4 color, vec4 background, vec2 position, float radius, float rays, float ray_length, sampler2D sound, vec2 uv)
{
    float inside = (1.0 - ray_length) * radius; //empty part of circle
    float outside = radius - inside; //rest of circle
    float circle = 2.0*M_PI*inside; //circle lenght
    for(int i = 1; float(i) <= rays; i++)
    {
        float len = outside * texture(sound, vec2(float(i)/rays, 0.0)).x; //length of actual ray
        background = bar(color, background, vec2(position.x, position.y+inside), vec2(circle/(rays*2.0), len), rotate(uv, position, 360.0/rays*float(i))); //Added capsules
    }
    return background; //output
}

vec4 bar(vec4 color, vec4 background, vec2 position, vec2 diemensions, vec2 uv)
{
    return capsule(color, background, vec4(position.x, position.y+diemensions.y/2.0, diemensions.x/2.0, diemensions.y/2.0), uv); //Just transform rectangle a little
}

vec4 capsule(vec4 color, vec4 background,  vec4 region, vec2 uv) //capsule
{
    if(uv.x > (region.x-region.z) && uv.x < (region.x+region.z) &&
       uv.y > (region.y-region.w) && uv.y < (region.y+region.w) || 
       distance(uv, region.xy - vec2(0.0, region.w)) < region.z || 
       distance(uv, region.xy + vec2(0.0, region.w)) < region.z)
        return color;
    return background;
}

vec2 rotate(vec2 point, vec2 center, float angle) //rotating point around the center
{
    float s = sin(radians(angle));
    float c = cos(radians(angle));

    point.x -= center.x;
    point.y -= center.y;

    float x = point.x * c - point.y * s;
    float y = point.x * s + point.y * c;

    point.x = x + center.x;
    point.y = y + center.y;

    return point;
}

