vec3 ltPos, qHit;
float tCur, tCyc, dstFar, dLoop, sLoop, rCyl, tubRot, bCylRad, bCylHt;
int idObj;
const float pi = 3.1415927, sqrt3 = 1.7320508;

#define VAR_ZERO min (iFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 b, c;
  float dMin, d, hBase, aLoop, lb, s, rc;
  dMin = dstFar;
  hBase = 1.;
  aLoop = 0.25 * pi / sLoop;
  rc = 0.01;
  q = p;
  q.y -= 2. * hBase;
  q.xz = Rot2D (q.xz, tubRot);
  q.xy = Rot2D (q.xy, 0.5 * pi);
  d = max (PrRoundBoxDf (vec3 (q.x, Rot2D (q.yz, - tubRot)), vec3 (rCyl - rc), rc),
     -0.01 - q.x);
  q.xy = Rot2D (vec2 (q.x, q.y - dLoop), aLoop - 0.5 * pi);
  b = vec2 (length (q.xy) - dLoop, q.z);
  b.xy = Rot2D (b.xy, tubRot);
  lb = length (b);
  s = PrRoundBox2Df (b, vec2 (rCyl - rc), rc);
  d = min (d, max (s, dot (vec2 (q.x, abs (q.y)), sin (aLoop + vec2 (0., 0.5 * pi)))));
  c = Rot2D (q.xy, aLoop) + vec2 (dLoop, 0.);
  s = max (PrRoundBoxDf (vec3 (Rot2D (vec2 (c.x, q.z), - tubRot), c.y).xzy,
     vec3 (rCyl - rc), rc), -0.01 - c.y);
  d = min (d, s);
  qHit = vec3 (vec2 (atan (q.y, - q.x) * dLoop / (0.25 * pi), atan (b.x, b.y)) / pi, lb);
  DMIN (1);
  q = p;
  q.y -= hBase - 0.5 * rCyl;
  d = PrRoundBoxDf (q, vec3 (0.8 * rCyl, hBase - 0.5 * rCyl, 0.8 * rCyl) - 0.05, 0.05);
  DMIN (2);
  return 0.8 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 150; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.005, -0.005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd, float dMax)
{
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05 || d > dMax) break;
  }
  return 0.5 + 0.5 * sh;
}

vec4 InCylHit (vec3 ro, vec3 rd)
{
  vec3 s, vn;
  float d, a, b, w, srdy;
  d = dstFar;
  vn = vec3 (0.);
  a = dot (rd.xz, rd.xz);
  b = dot (rd.xz, ro.xz);
  w = b * b - a * (dot (ro.xz, ro.xz) - bCylRad * bCylRad);
  if (w > 0.) {
    srdy = sign (rd.y);
    if (a > 0.) {
      d = (- b + sqrt (w)) / a;
      s = ro + d * rd;
    } else s.y = bCylHt;
    if (abs (s.y) < bCylHt) vn.xz = - s.xz / bCylRad;
    else {
      d = (- srdy * ro.y + bCylHt) / abs (rd.y);
      vn.y = - srdy;
    }
  }
  return vec4 (d, vn);
}

vec3 ShStagGrid (vec2 p)
{
  vec2 q, sq, ss;
  q = p;
  if (2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.5 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec4 vc, db4;
  vec3 ltDir, vn, col, rg, vnCyl;
  vec2 qw, wSize;
  float dCyl, hy, y, b, a, f, ga, ltDist, sh;
  bool isLit;
  isLit = true;
  db4 = InCylHit (ro + vec3 (0., - bCylHt, 0.), rd);
  dCyl = db4.x;
  vnCyl = db4.yzw;
  if (vnCyl.y == 0.) {
    ro += dCyl * rd;
    vn = vnCyl;
    hy = mod (ro.y / bCylHt + 0.5, 1.) - 0.5;
    a = atan (vn.x, - vn.z) / pi;
    wSize = vec2 (0.28, 0.33);
    qw = abs (vec2 (mod (32. * 0.5 * (1. + a) + 0.5, 1.), abs (hy)) - 0.5) - 0.5 * wSize;
    if (Maxv2 (abs (qw) - wSize * vec2 (0.43, 0.47)) < 0.) {
      col = vec3 (0.1, 0.2, 0.5);
      isLit = false;
    } else {
      col = vec3 (0.4);
      if (Maxv2 (qw - 0.5 * wSize) > 0.) {
        y = abs (abs (hy) - 0.5) - 0.5;
        if (y > - 0.3/16.) {
          col = vec3 (0.65);
          vn.xz = Rot2D (vn.xz, - pi * a);
          vn.yz = Rot2D (vn.yz, 0.15 * pi * (1. - 2. * SmoothBump (0.15, 0.25, 0.05,
             16. * (0.5 - y))) * sign (hy));
          vn.xz = Rot2D (vn.xz, pi * a);
        } else {
          col = vec3 (0.7, 0.75, 0.7);
          qw = 8. * vec2 (12. * a, 2. * hy);
          rg = ShStagGrid (qw);
          col *= rg.y * (1. - 0.2 * Noisefv2 (32. * qw));
          rg.xz *= sign ((abs (vn.x) > abs (vn.z)) ? vn.x : vn.z);
          if (abs (vn.x) > abs (vn.z)) {
            if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
            else vn.xz = Rot2D (vn.xz, rg.x);
          } else {
            if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
            else vn.zx = Rot2D (vn.zx, rg.x);
          }
        }
      }
    }
  } else if (vnCyl.y > 0.) {
    ro += dCyl * rd;
    vc = HexVor (0.7 * ro.zx);
    b = 1. - smoothstep (-0.2, -0.05, rd.y) * smoothstep (0.4, 0.8, dCyl / (1.6 * bCylRad));
    vn = mix (vec3 (0., 1., 0.), normalize (vec3 (- vc.y, 4., - vc.z)),
       b * SmoothBump (0.05, 0.13, 0.02, vc.x));
    col = vec3 (0.65, 0.62, 0.6) * (0.7 + 0.3 * b * step (0.06 + 0.03 * vc.w, vc.x) *
       (1. - 0.3 * Noisefv2 (16. * ro.xz)));
  } else {
    ro += dCyl * rd;
    f = 0.;
    qw = ro.xz / bCylRad;
    ga = 2.39996;
    for (float n = 0.; n < 128.; n ++) f += 1. - smoothstep (0.02, 0.025,
       length (qw - sqrt (n / 128.) * sin (n * ga + vec2 (0.5 * pi, 0.))));
    col = mix (vec3 (0.2, 0.2, 0.4), vec3 (1., 1., 0.7), f);
    isLit = false;
  }
  if (isLit) {
    ltDir = ltPos - ro;
    ltDist = length (ltDir);
    ltDir /= ltDist;
    sh = (vnCyl.y > -0.5) ? ObjSShadow (ro, ltDir, ltDist) : 1.;
    col *= 0.3 + 0.7 * sh * max (dot (vn, ltDir), 0.);
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn, ltDir;
  float dstObj, tCyc, t, sh, ltDist;
  bool doRefl;
  tCyc = 20.;
  t = tCur / tCyc;
  tubRot = mod (5. * t, 2. * pi);
  sLoop = 1./2. + 20. * pow (1. - SmoothBump (0.25, 0.75, 0.24, mod (t, 1.)), 8.);
  dLoop = 6. * sLoop;
  rCyl = 1.5;
  HexVorInit ();
  doRefl = false;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar && idObj == 1) {
    ro += dstObj * rd;
    doRefl = true;
    rd = reflect (rd, ObjNf (ro));
    ro += 0.01 * rd;
    dstObj = ObjRay (ro, rd);
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) col = vec3 (1.);
    else if (idObj == 2) col = vec3 (0.5);
    ltDir = ltPos - ro;
    ltDist = length (ltDir);
    ltDir /= ltDist;
    sh = ObjSShadow (ro, ltDir, ltDist);
    col = col * (0.3 + 0.7 * sh * max (dot (vn, ltDir), 0.));
  } else col = BgCol (ro, rd);
  if (doRefl) col = mix (col, 0.9 * BgCol (ro, rd), 0.9);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, t, tSmooth, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  el = -0.05 * pi;
  az = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.3 * pi * mPtr.y;
  } else {
    t = mod (0.005 * tCur, 2.);
    tSmooth = (floor (32. * t) + smoothstep (0.9, 1., mod (32. * t, 1.))) / 32.;
    az = 2.5 * pi * (0.5 - abs (tSmooth - 1.));
  }
  el = clamp (el, -0.2 * pi, -0.02 * pi);
  vuMat = StdVuMat (el, az);
  bCylRad = 32.;
  bCylHt = 12.;
  ro = vuMat * vec3 (0., 0., -0.95 * bCylRad);
  ro.y += 0.5 * bCylHt;
  zmFac = 3.5;
  dstFar = 3. * bCylRad;
  ltPos = vec3 (0., 1.5 * bCylHt, 0.9 * bCylRad);
  ltPos.xz = Rot2D (ltPos.xz, -0.2 * pi);
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec2 gVec[7], hVec[7];

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec4 HexVor (vec2 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.7;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
  sd = vec4 (4.);
  udm = vec4 (4.);
  for (int k = 0; k < 7; k ++) {
    u = Hashv2v2 (ip + gVec[k]);
    a = 2. * pi * (u.y - 0.5);
    d = hVec[k] + amp * (0.4 + 0.6 * u.x) * vec2 (cos (a), sin (a)) - fp;
    sd.w = dot (d, d);
    if (sd.w < sd.x) {
      sd = sd.wxyw;
      udm = vec4 (d, u);
    } else sd = (sd.w < sd.y) ? sd.xwyw : ((sd.w < sd.z) ? sd.xyww : sd);
  }
  sd.xyz = sqrt (sd.xyz);
  return vec4 (SmoothMin (sd.y, sd.z, 0.3) - sd.x, udm.xy, Hashfv2 (udm.zw));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

